import { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
// import DashboardNavbar from "../components/DashboardNavbar";
import { supabase } from '../lib/supabase';
import { FaChalkboardTeacher } from 'react-icons/fa';

const StudentDashboard = () => {
  const [classes, setClasses] = useState([]);
  const [grades, setGrades] = useState([]);
  const [attendance, setAttendance] = useState([]);
  const [activeTab, setActiveTab] = useState('classes');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [userData, setUserData] = useState(null);
  const navigate = useNavigate();

  // Get user data from session storage and check role
  useEffect(() => {
    const authData = sessionStorage.getItem('user_auth');
    if (authData) {
      const { user } = JSON.parse(authData);
      if (user.role !== 'student') {
        // Redirect non-students to home page
        navigate('/');
        return;
      }
      setUserData(user);
      fetchData(user.id);
    } else {
      // Redirect unauthenticated users to home page
      navigate('/');
    }
  }, [navigate]);

  const fetchData = async (studentId) => {
    try {
      setLoading(true);

      // Fetch classes
      const { data: classesData, error: classesError } = await supabase
        .from('student_enrollment')
        .select('*')
        .eq('student_id', studentId);

      if (classesError) throw classesError;
      setClasses(classesData || []);

      // Fetch grades
      const { data: gradesData, error: gradesError } = await supabase
        .from('grades')
        .select(`
          id,
          score,
          feedback,
          graded_at,
          assignment:assignments (
            id,
            title,
            max_score,
            level_code,
            category:grade_categories (
              name
            )
          )
        `)
        .eq('student_id', studentId)
        .order('graded_at', { ascending: false });

      if (gradesError) throw gradesError;
      setGrades(gradesData || []);

      // Fetch attendance
      const { data: attendanceData, error: attendanceError } = await supabase
        .from('attendance')
        .select('*')
        .eq('student_id', studentId);

      if (attendanceError) throw attendanceError;
      setAttendance(attendanceData || []);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // Student Info Component
  const StudentInfo = () => (
    <div className="bg-gradient-to-r from-blue-50 to-blue-100 p-8 rounded-2xl shadow mb-8 flex flex-col md:flex-row items-center justify-between">
      <div>
        <h2 className="text-2xl font-bold mb-2 text-blue-900">Student Information</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="space-y-2">
            <div>
              <label className="text-xs font-medium text-gray-500">Full Name</label>
              <p className="text-lg text-gray-800">{userData?.name || 'N/A'}</p>
            </div>
            <div>
              <label className="text-xs font-medium text-gray-500">Student ID</label>
              <p className="text-lg text-gray-800">{userData?.id || 'N/A'}</p>
            </div>
          </div>
          <div className="space-y-2">
            <div>
              <label className="text-xs font-medium text-gray-500">Username</label>
              <p className="text-lg text-gray-800">{userData?.username || 'N/A'}</p>
            </div>
            <div>
              <label className="text-xs font-medium text-gray-500">Role</label>
              <p className="text-lg text-gray-800 capitalize">{userData?.role || 'N/A'}</p>
            </div>
          </div>
        </div>
      </div>
      <Link
        to={userData?.id ? `/level/${userData.id}` : "#"}
        className="mt-6 md:mt-0 inline-flex items-center gap-2 px-6 py-3 bg-blue-600 text-white font-semibold rounded-lg shadow hover:bg-blue-700 transition"
      >
        <FaChalkboardTeacher className="text-xl" />
        View My Classes
      </Link>
    </div>
  );

  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  const renderTabContent = () => {
    if (error) {
      return (
        <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 rounded mb-6">
          <p>Error: {error}</p>
        </div>
      );
    }

    switch (activeTab) {
      case 'grades':
        return (
          <div className="bg-white p-8 rounded-2xl shadow mb-8">
            <h2 className="text-2xl font-semibold mb-6 text-blue-900">Your Grades</h2>
            {grades.length > 0 ? (
              <div className="overflow-x-auto">
                <table className="min-w-full border rounded-xl">
                  <thead>
                    <tr className="bg-blue-100">
                      <th className="px-6 py-3 text-center font-semibold whitespace-nowrap border-b">Assignment</th>
                      <th className="px-6 py-3 text-center font-semibold whitespace-nowrap border-b">Level</th>
                      <th className="px-6 py-3 text-center font-semibold whitespace-nowrap border-b">Type</th>
                      <th className="px-6 py-3 text-center font-semibold whitespace-nowrap border-b">Score</th>
                      <th className="px-6 py-3 text-center font-semibold whitespace-nowrap border-b">Feedback</th>
                      <th className="px-6 py-3 text-center font-semibold whitespace-nowrap border-b">Date</th>
                    </tr>
                  </thead>
                  <tbody>
                    {grades.map((grade) => (
                      <tr key={grade.id} className="border-b hover:bg-blue-50 transition">
                        <td className="px-6 py-2 text-center">{grade.assignment?.title}</td>
                        <td className="px-6 py-2 text-center">{grade.assignment?.level_code}</td>
                        <td className="px-6 py-2 text-center">{grade.assignment?.category?.name || '-'}</td>
                        <td className="px-6 py-2 text-center">{grade.score} / {grade.assignment?.max_score}</td>
                        <td className="px-6 py-2 text-center">{grade.feedback || '-'}</td>
                        <td className="px-6 py-2 text-center">{new Date(grade.graded_at).toLocaleDateString()}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ) : (
              <p className="text-gray-600 text-center">No grades available.</p>
            )}
          </div>
        );

      case 'attendance':
        return (
          <div className="bg-white p-8 rounded-2xl shadow mb-8">
            <h2 className="text-2xl font-semibold mb-6 text-blue-900">Attendance Record</h2>
            {attendance.length > 0 ? (
              <div className="overflow-x-auto">
                <table className="min-w-full border rounded-xl">
                  <thead>
                    <tr className="bg-blue-100">
                      <th className="px-6 py-3 text-center font-semibold whitespace-nowrap border-b">Date</th>
                      <th className="px-6 py-3 text-center font-semibold whitespace-nowrap border-b">Subject</th>
                      <th className="px-6 py-3 text-center font-semibold whitespace-nowrap border-b">Status</th>
                      <th className="px-6 py-3 text-center font-semibold whitespace-nowrap border-b">Remarks</th>
                    </tr>
                  </thead>
                  <tbody>
                    {attendance.map((record) => (
                      <tr key={record.id} className="border-b hover:bg-blue-50 transition">
                        <td className="px-6 py-2 text-center">{new Date(record.date).toLocaleDateString()}</td>
                        <td className="px-6 py-2 text-center">{record.level_code}</td>
                        <td className="px-6 py-2 text-center">
                          <span className={`px-2 py-1 rounded ${
                            record.status === 'present' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                          }`}>
                            {record.status}
                          </span>
                        </td>
                        <td className="px-6 py-2 text-center">{record.remarks || '-'}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ) : (
              <p className="text-gray-600 text-center">No attendance records available.</p>
            )}
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-blue-200 py-10">
      <div className="p-4 max-w-5xl mx-auto">
        <h1 className="text-4xl font-extrabold mb-8 text-center text-blue-900 drop-shadow">Student Dashboard</h1>
        <StudentInfo />
        {/* Navigation Tabs */}
        <div className="flex justify-center mb-8">
          <div className="inline-flex rounded-lg shadow bg-white">
            <button
              onClick={() => setActiveTab('grades')}
              className={`px-6 py-3 text-md font-medium rounded-l-lg transition ${
                activeTab === 'grades'
                  ? 'bg-blue-600 text-white'
                  : 'bg-white text-gray-700 hover:bg-blue-50'
              }`}
            >
              Grades
            </button>
            <button
              onClick={() => setActiveTab('attendance')}
              className={`px-6 py-3 text-md font-medium rounded-r-lg transition ${
                activeTab === 'attendance'
                  ? 'bg-blue-600 text-white'
                  : 'bg-white text-gray-700 hover:bg-blue-50'
              }`}
            >
              Attendance
            </button>
          </div>
        </div>
        {/* Tab Content */}
        {renderTabContent()}
      </div>
    </div>
  );
};

export default StudentDashboard;
