import React, { useState, useEffect } from 'react';
import AdminSidebar from '../../components/AdminSidebar';
import { Link, useLocation } from 'react-router-dom';


const ManagePayments = () => {
  const location = useLocation();
  const [payments, setPayments] = useState([]);
  const [showPaymentModal, setShowPaymentModal] = useState(false);
  const [newPayment, setNewPayment] = useState({ parent: '', amount: '', date: '' });
  const [editPayment, setEditPayment] = useState(null);

  useEffect(() => {
    fetchPayments();
  }, []);

  const fetchPayments = async () => {
    try {
      const response = await fetch('http://localhost:5100/api/payments');
      if (!response.ok) throw new Error('Failed to fetch payments');
      const data = await response.json();
      setPayments(data);
    } catch (error) {
      console.error('Error fetching payments:', error);
    }
  };

  const handleCreatePayment = async () => {
    if (!newPayment.parent || !newPayment.amount || !newPayment.date) {
      alert("Please fill in all required fields");
      return;
    }
    const response = await fetch('http://localhost:5100/api/payments', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(newPayment),
    });
    if (response.ok) {
      fetchPayments();
    }
    setNewPayment({ parent: '', amount: '', date: '' });
    setShowPaymentModal(false);
  };

  const handleEditPayment = (payment) => {
    setEditPayment(payment);
    setShowPaymentModal(true);
  };

  const handleUpdatePayment = async () => {
    if (!editPayment.parent || !editPayment.amount || !editPayment.date) {
      alert("Please fill in all required fields");
      return;
    }
    const response = await fetch(`http://localhost:5100/api/payments/${editPayment.id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(editPayment),
    });
    if (response.ok) {
      fetchPayments();
    }
    setEditPayment(null);
    setShowPaymentModal(false);
  };

  return (
    <div className="flex h-screen">
      <aside className="w-64 bg-gray-900 text-white flex flex-col p-5">
        <AdminSidebar location={location} />
      </aside>
      <main className="flex-1 p-6">
        <h2 className="text-2xl font-bold mb-4">Payment Tracking</h2>
        <div className="flex justify-between mb-4">
          <button onClick={() => setShowPaymentModal(true)} className="bg-blue-500 text-white p-4 rounded text-center hover:bg-blue-600">âž• Create Payment</button>
        </div>
        <table className="min-w-full border-collapse border border-gray-300">
          <thead>
            <tr>
              <th className="border border-gray-300 p-2">ID</th>
              <th className="border border-gray-300 p-2">Parent</th>
              <th className="border border-gray-300 p-2">Amount</th>
              <th className="border border-gray-300 p-2">Date</th>
              <th className="border border-gray-300 p-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {payments.map(payment => (
              <tr key={payment.id}>
                <td className="border border-gray-300 p-2">{payment.id}</td>
                <td className="border border-gray-300 p-2">{payment.parent}</td>
                <td className="border border-gray-300 p-2">${payment.amount}</td>
                <td className="border border-gray-300 p-2">{payment.date}</td>
                <td className="border border-gray-300 p-2">
                  <button onClick={() => handleEditPayment(payment)} className="text-blue-500">Edit</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {showPaymentModal && (
          <div className="fixed inset-0 flex items-center justify-center bg-gray-800 bg-opacity-50">
            <div className="bg-white p-6 rounded shadow-lg w-1/3">
              <h2 className="text-xl font-bold mb-4">{editPayment ? 'Edit Payment' : 'Create Payment'}</h2>
              <input type="text" placeholder="Parent" className="w-full p-2 border rounded mb-2" value={editPayment ? editPayment.parent : newPayment.parent} onChange={(e) => editPayment ? setEditPayment({ ...editPayment, parent: e.target.value }) : setNewPayment({ ...newPayment, parent: e.target.value })} />
              <input type="number" placeholder="Amount" className="w-full p-2 border rounded mb-2" value={editPayment ? editPayment.amount : newPayment.amount} onChange={(e) => editPayment ? setEditPayment({ ...editPayment, amount: e.target.value }) : setNewPayment({ ...newPayment, amount: e.target.value })} />
              <input type="date" placeholder="Date" className="w-full p-2 border rounded mb-2" value={editPayment ? editPayment.date : newPayment.date} onChange={(e) => editPayment ? setEditPayment({ ...editPayment, date: e.target.value }) : setNewPayment({ ...newPayment, date: e.target.value })} />
              <div className="flex justify-between">
                <button onClick={editPayment ? handleUpdatePayment : handleCreatePayment} className="bg-green-500 text-white px-4 py-2 rounded">{editPayment ? 'Update' : 'Create'}</button>
                <button onClick={() => { setShowPaymentModal(false); setEditPayment(null); }} className="bg-gray-400 text-white px-4 py-2 rounded">Cancel</button>
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default ManagePayments;
