import { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';

const InstructorDashboard = () => {
  const location = useLocation();
  const [classes, setClasses] = useState([]);
  const [selectedClass, setSelectedClass] = useState(null);
  const [students, setStudents] = useState([]);
  const [attendance, setAttendance] = useState({});

  useEffect(() => {
    fetchClasses();
  }, []);

  const fetchClasses = async () => {
    try {
      const response = await fetch('http://localhost:5100/api/instructor/classes');
      if (!response.ok) throw new Error('Failed to fetch classes');
      const data = await response.json();
      setClasses(data);
    } catch (error) {
      console.error('Error fetching classes:', error);
    }
  };

  const fetchStudents = async (classId) => {
    setSelectedClass(classId);
    try {
      const response = await fetch(`http://localhost:5100/api/instructor/classes/${classId}/students`);
      if (!response.ok) throw new Error('Failed to fetch students');
      const data = await response.json();
      setStudents(data);
    } catch (error) {
      console.error('Error fetching students:', error);
    }
  };

  const handleTakeAttendance = async (studentId, status) => {
    try {
      await fetch('http://localhost:5100/api/instructor/attendance', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ class_id: selectedClass, student_id: studentId, status }),
      });
      setAttendance({ ...attendance, [studentId]: status });
    } catch (error) {
      console.error('Error updating attendance:', error);
    }
  };

  return (
    <div className="flex h-screen">
      <aside className="w-64 bg-gray-900 text-white flex flex-col p-5">
        <h2 className="text-2xl font-bold mb-6">Instructor Panel</h2>
        <nav className="space-y-3">
          <Link to="/instructor" className={`block p-2 rounded ${location.pathname === '/instructor' ? 'bg-gray-700' : 'hover:bg-gray-700'}`}>üìä Dashboard</Link>
          <Link to="/instructor/manage-classes" className={`block p-2 rounded ${location.pathname === '/instructor/manage-classes' ? 'bg-gray-700' : 'hover:bg-gray-700'}`}>üè´ Manage Classes</Link>
        </nav>
      </aside>

      <main className="flex-1 p-6">
        <h1 className="text-3xl font-bold mb-6">Instructor Dashboard</h1>
        <h2 className="text-xl font-bold mb-4">Your Classes</h2>
        <ul>
          {classes.map((c) => (
            <li key={c.id} className="p-2 bg-gray-100 my-2 rounded cursor-pointer" onClick={() => fetchStudents(c.id)}>
              {c.name} - {c.period}
            </li>
          ))}
        </ul>

        {selectedClass && (
          <div className="mt-6">
            <h2 className="text-xl font-bold mb-4">Students in Class</h2>
            <ul>
              {students.map((student) => (
                <li key={student.id} className="flex justify-between items-center p-2 border-b">
                  {student.first_name} {student.last_name}
                  <div>
                    <button onClick={() => handleTakeAttendance(student.id, 'present')} className="px-3 py-1 bg-green-500 text-white rounded">Present</button>
                    <button onClick={() => handleTakeAttendance(student.id, 'absent')} className="px-3 py-1 bg-red-500 text-white rounded ml-2">Absent</button>
                  </div>
                </li>
              ))}
            </ul>
          </div>
        )}
      </main>
    </div>
  );
};

export default InstructorDashboard;
