import { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';

const InstructorDashboard = () => {
  const location = useLocation();
  const [classes, setClasses] = useState([]);
  const [selectedClass, setSelectedClass] = useState(null);
  const [students, setStudents] = useState([]);
  const [attendance, setAttendance] = useState({});
  const [updates, setUpdates] = useState([]);
  const [newUpdate, setNewUpdate] = useState({ content: '', type: 'announcement' });

  useEffect(() => {
    fetchClasses();
  }, []);

  const fetchClasses = async () => {
    try {
      const user = JSON.parse(localStorage.getItem('user'));
      const response = await fetch(`http://localhost:5100/api/classes/instructor/${user.id}`);
      if (!response.ok) throw new Error('Failed to fetch classes');
      const data = await response.json();
      setClasses(data);
    } catch (error) {
      console.error('Error fetching classes:', error);
    }
  };

  const fetchStudents = async (classId) => {
    setSelectedClass(classId);
    try {
      const response = await fetch(`http://localhost:5100/api/instructor/classes/${classId}/students`);
      if (!response.ok) throw new Error('Failed to fetch students');
      const data = await response.json();
      setStudents(data);
    } catch (error) {
      console.error('Error fetching students:', error);
    }
  };

  return (
    <div className="flex h-screen">
      <aside className="w-64 bg-gray-900 text-white flex flex-col p-5">
        <h2 className="text-2xl font-bold mb-6">Instructor Panel</h2>
        <nav className="space-y-3">
          <Link to="/instructor" className={`block p-2 rounded ${location.pathname === '/instructor' ? 'bg-gray-700' : 'hover:bg-gray-700'}`}>📊 Dashboard</Link>
          {classes.map(c => (
            <Link key={c.id} to={`/instructor/classes/${c.id}`} className={`block p-2 rounded ${selectedClass === c.id ? 'bg-gray-700' : 'hover:bg-gray-700'}`} onClick={() => fetchStudents(c.id)}>
              🏫 {c.name}
            </Link>
          ))}
          <Link to="/instructor/grades" className={`block p-2 rounded ${location.pathname === '/instructor/grades' ? 'bg-gray-700' : 'hover:bg-gray-700'}`}>📑 Manage Grades</Link>
        </nav>
      </aside>

      <main className="flex-1 p-6">
        <h1 className="text-3xl font-bold mb-6">Instructor Dashboard</h1>

        {selectedClass && (
          <div className="mt-6">
            <h2 className="text-xl font-bold mb-4">Students in Class</h2>
            <ul>
              {students.map((student) => (
                <li key={student.id} className="flex justify-between items-center p-2 border-b">
                  {student.first_name} {student.last_name}
                </li>
              ))}
            </ul>

            <h2 className="text-xl font-bold mb-4 mt-6">Post an Update</h2>
            <select className="w-full p-2 border rounded mb-3" value={newUpdate.type} onChange={(e) => setNewUpdate({ ...newUpdate, type: e.target.value })}>
              <option value="announcement">Announcement</option>
              <option value="homework">Homework</option>
              <option value="classwork">Classwork</option>
              <option value="exam">Exam</option>
            </select>
            <textarea
              className="w-full p-2 border rounded mb-3"
              placeholder="Write your update here..."
              value={newUpdate.content}
              onChange={(e) => setNewUpdate({ ...newUpdate, content: e.target.value })}
            />
            <button className="w-full bg-blue-500 text-white p-2 rounded">Post Update</button>
          </div>
        )}
      </main>
    </div>
  );
};

export default InstructorDashboard;
