import { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';

const InstructorDashboard = () => {
  const location = useLocation();
  const [classes, setClasses] = useState([]);
  const [latestUpdates, setLatestUpdates] = useState([]);
  const [selectedClass, setSelectedClass] = useState(null);
  const [students, setStudents] = useState([]);
  const [newUpdate, setNewUpdate] = useState({ content: '', type: 'announcement' });

  useEffect(() => {
    fetchClasses();
    fetchLatestUpdates();
  }, []);

  const fetchClasses = async () => {
    try {
      const user = JSON.parse(localStorage.getItem('user'));
      const response = await fetch(`http://localhost:5100/api/classes/instructor/${user.id}`);
      if (!response.ok) throw new Error('Failed to fetch classes');
      const data = await response.json();
      setClasses(data);
    } catch (error) {
      console.error('Error fetching classes:', error);
    }
  };

  const fetchLatestUpdates = async () => {
    try {
      const response = await fetch(`http://localhost:5100/api/instructor/latest-updates`);
      if (!response.ok) throw new Error('Failed to fetch updates');
      const data = await response.json();
      setLatestUpdates(data);
    } catch (error) {
      console.error('Error fetching updates:', error);
    }
  };

  return (
    <div className="flex h-screen">
      <aside className="w-64 bg-gray-900 text-white flex flex-col p-5">
        <h2 className="text-2xl font-bold mb-6">Instructor Panel</h2>
        <nav className="space-y-3">
          <Link to="/instructor" className={`block p-2 rounded ${location.pathname === '/instructor' ? 'bg-gray-700' : 'hover:bg-gray-700'}`}>ðŸ“Š Dashboard</Link>
          <Link to="/instructor/grades" className={`block p-2 rounded ${location.pathname === '/instructor/grades' ? 'bg-gray-700' : 'hover:bg-gray-700'}`}>ðŸ“‘ Manage Grades</Link>
        </nav>
      </aside>

      <main className="flex-1 p-6">
        <h1 className="text-3xl font-bold mb-6">Instructor Dashboard</h1>

        {/* Summary of Classes */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
          {classes.map((c) => (
            <div key={c.id} className="bg-white p-4 shadow rounded flex justify-between items-center">
              <div>
                <h2 className="text-lg font-semibold">{c.name}</h2>
                <p className="text-gray-600">{c.subject} - {c.period}</p>
              </div>
              <Link to={`/instructor/classes/${c.id}`} className="text-blue-500">View</Link>
            </div>
          ))}
        </div>

        {/* Latest Updates */}
        <div className="bg-white p-6 rounded shadow mb-6">
          <h2 className="text-xl font-bold mb-4">Latest Updates</h2>
          <ul>
            {latestUpdates.map((update) => (
              <li key={update.id} className="p-2 border-b">{update.type}: {update.content}</li>
            ))}
          </ul>
        </div>

        {/* Quick Actions */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <Link to="/instructor/take-attendance" className="bg-green-500 text-white p-4 rounded text-center hover:bg-green-600">ðŸ“‹ Take Attendance</Link>
          <Link to="/instructor/post-update" className="bg-blue-500 text-white p-4 rounded text-center hover:bg-blue-600">ðŸ“¢ Post Update</Link>
        </div>
      </main>
    </div>
  );
};

export default InstructorDashboard;
