import React from 'react';

const ManagePayments = () => {
  // Sample data for payments (this would typically come from an API)
  const payments = [
    { id: 1, parent: 'John Doe', amount: 100, date: '2023-10-01' },
    { id: 2, parent: 'Jane Smith', amount: 150, date: '2023-10-02' },
    // ... more payment records
  ];

  return (
    <div className="p-4">

      <h2 className="text-2xl font-bold mb-4">Payment Tracking</h2>
      <table className="min-w-full border-collapse border border-gray-300">
        <thead>
          <tr>
            <th className="border border-gray-300 p-2">ID</th>
            <th className="border border-gray-300 p-2">Parent</th>
            <th className="border border-gray-300 p-2">Amount</th>
            <th className="border border-gray-300 p-2">Date</th>
          </tr>
        </thead>
        <tbody>
          {payments.map(payment => (
            <tr key={payment.id}>
              <td className="border border-gray-300 p-2">{payment.id}</td>
              <td className="border border-gray-300 p-2">{payment.parent}</td>
              <td className="border border-gray-300 p-2">${payment.amount}</td>
              <td className="border border-gray-300 p-2">{payment.date}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ManagePayments;
