import { useEffect, useState } from 'react';

const InstructorDashboard = () => {
  const [classes, setClasses] = useState([]);

  useEffect(() => {
    const fetchClasses = async () => {
      const user = JSON.parse(localStorage.getItem('user'));
      const response = await fetch(`http://localhost:5000/api/classes/user-classes/${user.id}/instructor`);
      const data = await response.json();
      setClasses(data);
    };

    fetchClasses();
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-4">Instructor Dashboard</h1>
      <h2 className="text-xl mb-3">Classes You Teach</h2>
      <ul>
        {classes.map((c) => (
          <li key={c.id} className="p-2 bg-gray-100 my-2 rounded">
            {c.name} - {c.trimester}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default InstructorDashboard;
