import { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import AdminSidebar from '../../components/AdminSidebar';

const ManageClasses = () => {
  const location = useLocation();
  const [classes, setClasses] = useState([]);
  const [instructors, setInstructors] = useState([]);
  const [showClassModal, setShowClassModal] = useState(false);
  const [newClass, setNewClass] = useState({ name: '', subject: '', period: '', instructor_id: '' });
  const [editClass, setEditClass] = useState(null);

  useEffect(() => {
    fetchInstructors();
  }, []);

  useEffect(() => {
    if (instructors.length > 0) {
      fetchClasses();
    }
  }, [instructors]);

  const fetchClasses = async () => {
    try {
      const response = await fetch('http://localhost:5100/api/classes');
      if (!response.ok) throw new Error('Failed to fetch classes');
      const data = await response.json();

      const updatedClasses = data.map(classItem => {
        const instructor = instructors.find(inst => inst.id === classItem.instructor_id);
        return {
          ...classItem,
          instructor_name: instructor ? `${instructor.firstName} ${instructor.lastName}` : 'Unknown Instructor'
        };
      });
      setClasses(updatedClasses);
    } catch (error) {
      console.error('Error fetching classes:', error);
    }
  };

  const fetchInstructors = async () => {
    try {
      const response = await fetch('http://localhost:5100/api/users/instructors');
      if (!response.ok) throw new Error('Failed to fetch instructors');
      const data = await response.json();
      setInstructors(data);
    } catch (error) {
      console.error('Error fetching instructors:', error);
    }
  };

  const handleCreateClass = async () => {
    if (!newClass.name || !newClass.subject || !newClass.period || !newClass.instructor_id) {
      alert("Please fill in all required fields");
      return;
    }
    const response = await fetch('http://localhost:5100/api/classes', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(newClass),
    });
    if (response.ok) {
      fetchClasses();
    }
    setNewClass({ name: '', subject: '', period: '', instructor_id: '' });
    setShowClassModal(false);
  };

  const handleEditClass = (classItem) => {
    setEditClass(classItem);
    setShowClassModal(true);
  };

  const handleUpdateClass = async () => {
    if (!editClass.name || !editClass.subject || !editClass.period || !editClass.instructor_id) {
      alert("Please fill in all required fields");
      return;
    }
    const response = await fetch(`http://localhost:5100/api/admin/classes/${editClass.id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(editClass),
    });
    if (response.ok) {
      fetchClasses();
    }
    setEditClass(null);
    setShowClassModal(false);
  };

  return (
    <div className="flex h-screen">
      <aside className="w-64 bg-gray-900 text-white flex flex-col p-5">
          <AdminSidebar location={location} />
        </aside>
      <main className="flex-1 p-6">
        <h1 className="text-3xl font-bold mb-6">Manage Classes</h1>
        <div className="flex justify-between mb-4">
          <button onClick={() => setShowClassModal(true)} className="bg-blue-500 text-white p-4 rounded text-center hover:bg-blue-600">âž• Create Class</button>
        </div>
        <div className="mt-6 bg-white p-6 rounded shadow">
          <h2 className="text-xl font-bold mb-4">Class List</h2>
          <table className="w-full border-collapse border border-gray-300">
            <thead>
              <tr className="bg-gray-100">
                <th className="border p-2">Class Name</th>
                <th className="border p-2">Subject</th>
                <th className="border p-2">Period</th>
                <th className="border p-2">Instructor</th>
                <th className="border p-2">Actions</th>
              </tr>
            </thead>
            <tbody>
              {classes.map(classItem => (
                <tr key={classItem.id} className="border">
                  <td className="border p-2 font-semibold">{classItem.name}</td>
                  <td className="border p-2">{classItem.subject}</td>
                  <td className="border p-2">{classItem.period}</td>
                  <td className="border p-2">{classItem.instructor_name}</td>
                  <td className="border p-2">
                    <button onClick={() => handleEditClass(classItem)} className="text-blue-500">Edit</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Create Class Modal */}
        {showClassModal && (
          <div className="fixed inset-0 flex items-center justify-center bg-gray-800 bg-opacity-50">
            <div className="bg-white p-6 rounded shadow-lg w-1/3">
              <h2 className="text-xl font-bold mb-4">{editClass ? 'Edit Class' : 'Create Class'}</h2>
              <input type="text" placeholder="Class Name" className="w-full p-2 border rounded mb-2" value={editClass ? editClass.name : newClass.name} onChange={(e) => editClass ? setEditClass({ ...editClass, name: e.target.value }) : setNewClass({ ...newClass, name: e.target.value })} />
              <input type="text" placeholder="Subject" className="w-full p-2 border rounded mb-2" value={editClass ? editClass.subject : newClass.subject} onChange={(e) => editClass ? setEditClass({ ...editClass, subject: e.target.value }) : setNewClass({ ...newClass, subject: e.target.value })} />
              <input type="text" placeholder="Period" className="w-full p-2 border rounded mb-2" value={editClass ? editClass.period : newClass.period} onChange={(e) => editClass ? setEditClass({ ...editClass, period: e.target.value }) : setNewClass({ ...newClass, period: e.target.value })} />
              <select className="w-full p-2 border rounded mb-2" value={editClass ? editClass.instructor_id : newClass.instructor_id} onChange={(e) => editClass ? setEditClass({ ...editClass, instructor_id: e.target.value }) : setNewClass({ ...newClass, instructor_id: e.target.value })}>
                <option value="">Select Instructor</option>
                {instructors.map(instructor => (
                  <option key={instructor.id} value={instructor.id}>{instructor.firstName} {instructor.lastName}</option>
                ))}
              </select>
              <div className="flex justify-between">
                <button onClick={editClass ? handleUpdateClass : handleCreateClass} className="bg-green-500 text-white px-4 py-2 rounded">{editClass ? 'Update' : 'Create'}</button>
                <button onClick={() => { setShowClassModal(false); setEditClass(null); }} className="bg-gray-400 text-white px-4 py-2 rounded">Cancel</button>
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default ManageClasses;
