import { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
// import DashboardNavbar from "../components/DashboardNavbar";
import { supabase } from '../lib/supabase';

const StudentDashboard = () => {
  const navigate = useNavigate();
  const [classes, setClasses] = useState([]);
  const [grades, setGrades] = useState([]);
  const [attendance, setAttendance] = useState([]);
  const [activeTab, setActiveTab] = useState('classes');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [userData, setUserData] = useState(null);
  const [showPasswordModal, setShowPasswordModal] = useState(false);
  const [password, setPassword] = useState('');
  const [passwordError, setPasswordError] = useState(null);

  // Check if student is already authenticated
  useEffect(() => {
    const checkAuth = async () => {
      const authData = sessionStorage.getItem('student_auth');
      if (authData) {
        const { timestamp, user } = JSON.parse(authData);
        // Check if 5 minutes have passed
        if (Date.now() - timestamp < 5 * 60 * 1000) {
          // Still valid, set user data and proceed
          setUserData(user);
          setLoading(false);
          return;
        } else {
          // Expired, remove from session
          sessionStorage.removeItem('student_auth');
        }
      }
      // Not authenticated or expired, show password modal
      setShowPasswordModal(true);
      setLoading(false);
    };

    checkAuth();
  }, []);

  const handlePasswordSubmit = async (e) => {
    e.preventDefault();
    setPasswordError(null);

    try {
      // Check the users table for the code (passcode)
      const { data: userData, error } = await supabase
        .from('users')
        .select('id, name, username, role, code')
        .eq('code', password)
        .single();

      if (error) throw error;

      if (userData && userData.role === 'student') {
        // Store in session storage
        sessionStorage.setItem('student_auth', JSON.stringify({
          timestamp: Date.now(),
          user: userData
        }));

        setUserData(userData);
        setShowPasswordModal(false);
        fetchData(userData.id);
      } else {
        setPasswordError('Invalid student code');
      }
    } catch (err) {
      setPasswordError(err.message);
    }
  };

  const fetchData = async (studentId) => {
    try {
      setLoading(true);

      // Fetch classes
      const { data: classesData, error: classesError } = await supabase
        .from('student_enrollment')
        .select('*')
        .eq('student_id', studentId);

      if (classesError) throw classesError;
      setClasses(classesData || []);

      // Fetch grades
      const { data: gradesData, error: gradesError } = await supabase
        .from('grades')
        .select('*')
        .eq('student_id', studentId);

      if (gradesError) throw gradesError;
      setGrades(gradesData || []);

      // Fetch attendance
      const { data: attendanceData, error: attendanceError } = await supabase
        .from('attendance')
        .select('*')
        .eq('student_id', studentId);

      if (attendanceError) throw attendanceError;
      setAttendance(attendanceData || []);

    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // Password Modal Component
  const PasswordModal = () => (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
      <div className="bg-white p-6 rounded-lg shadow-xl max-w-md w-full">
        <h2 className="text-2xl font-bold mb-4">Enter Student Code</h2>
        <form onSubmit={handlePasswordSubmit}>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full p-2 border rounded mb-4"
            placeholder="Enter your student code"
            required
          />
          {passwordError && (
            <p className="text-red-500 mb-4">{passwordError}</p>
          )}
          <button
            type="submit"
            className="w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600"
          >
            Submit
          </button>
        </form>
      </div>
    </div>
  );

  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  if (showPasswordModal) {
    return <PasswordModal />;
  }

  const renderTabContent = () => {
    if (loading) {
      return (
        <div className="flex justify-center items-center h-64">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
        </div>
      );
    }

    if (error) {
      return (
        <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4">
          <p>Error: {error}</p>
        </div>
      );
    }

    switch (activeTab) {
      case 'classes':
        return (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {classes.length > 0 ? (
              classes.map((c) => (
                <div key={c.id} className="bg-white p-4 shadow rounded">
                  <h2 className="text-xl font-semibold">{c.name}</h2>
                  <p className="text-gray-700">{c.trimester}</p>
                  <Link to={`/class/${c.id}`} className="block text-center mt-4 bg-blue-500 text-white py-2 rounded">
                    View Class
                  </Link>
                </div>
              ))
            ) : (
              <p className="text-gray-600 text-center">You are not enrolled in any classes.</p>
            )}
          </div>
        );

      case 'grades':
        return (
          <div className="bg-white p-6 rounded shadow">
            <h2 className="text-2xl font-semibold mb-4">Your Grades</h2>
            {grades.length > 0 ? (
              <div className="overflow-x-auto">
                <table className="min-w-full">
                  <thead>
                    <tr className="bg-gray-100">
                      <th className="px-4 py-2">Subject</th>
                      <th className="px-4 py-2">Grade</th>
                      <th className="px-4 py-2">Term</th>
                      <th className="px-4 py-2">Date</th>
                    </tr>
                  </thead>
                  <tbody>
                    {grades.map((grade) => (
                      <tr key={grade.id} className="border-b">
                        <td className="px-4 py-2">{grade.subject}</td>
                        <td className="px-4 py-2">{grade.grade}</td>
                        <td className="px-4 py-2">{grade.term}</td>
                        <td className="px-4 py-2">{new Date(grade.created_at).toLocaleDateString()}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ) : (
              <p className="text-gray-600 text-center">No grades available.</p>
            )}
          </div>
        );

      case 'attendance':
        return (
          <div className="bg-white p-6 rounded shadow">
            <h2 className="text-2xl font-semibold mb-4">Attendance Record</h2>
            {attendance.length > 0 ? (
              <div className="overflow-x-auto">
                <table className="min-w-full">
                  <thead>
                    <tr className="bg-gray-100">
                      <th className="px-4 py-2">Date</th>
                      <th className="px-4 py-2">Subject</th>
                      <th className="px-4 py-2">Status</th>
                      <th className="px-4 py-2">Remarks</th>
                    </tr>
                  </thead>
                  <tbody>
                    {attendance.map((record) => (
                      <tr key={record.id} className="border-b">
                        <td className="px-4 py-2">{new Date(record.date).toLocaleDateString()}</td>
                        <td className="px-4 py-2">{record.subject}</td>
                        <td className="px-4 py-2">
                          <span className={`px-2 py-1 rounded ${
                            record.status === 'present' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                          }`}>
                            {record.status}
                          </span>
                        </td>
                        <td className="px-4 py-2">{record.remarks || '-'}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ) : (
              <p className="text-gray-600 text-center">No attendance records available.</p>
            )}
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <h1 className="text-3xl font-bold mb-6 text-center">Student Dashboard</h1>

      {/* Navigation Tabs */}
      <div className="flex justify-center mb-8">
        <div className="inline-flex rounded-md shadow-sm">
          <button
            onClick={() => setActiveTab('classes')}
            className={`px-4 py-2 text-sm font-medium rounded-l-lg ${
              activeTab === 'classes'
                ? 'bg-blue-500 text-white'
                : 'bg-white text-gray-700 hover:bg-gray-50'
            }`}
          >
            Classes
          </button>
          <button
            onClick={() => setActiveTab('grades')}
            className={`px-4 py-2 text-sm font-medium ${
              activeTab === 'grades'
                ? 'bg-blue-500 text-white'
                : 'bg-white text-gray-700 hover:bg-gray-50'
            }`}
          >
            Grades
          </button>
          <button
            onClick={() => setActiveTab('attendance')}
            className={`px-4 py-2 text-sm font-medium rounded-r-lg ${
              activeTab === 'attendance'
                ? 'bg-blue-500 text-white'
                : 'bg-white text-gray-700 hover:bg-gray-50'
            }`}
          >
            Attendance
          </button>
        </div>
      </div>

      {/* Tab Content */}
      {renderTabContent()}
    </div>
  );
};

export default StudentDashboard;
