import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const InstructorDashboard = () => {
  const [classes, setClasses] = useState([]);
  const [selectedClass, setSelectedClass] = useState(null);
  const [students, setStudents] = useState([]);
  const [attendance, setAttendance] = useState({});
  const [date, setDate] = useState(new Date().toISOString().split('T')[0]);

  useEffect(() => {
    const fetchClasses = async () => {
      const user = JSON.parse(localStorage.getItem('user'));
      const response = await fetch(`http://localhost:5100/api/classes/user-classes/${user.id}/instructor`);
      const data = await response.json();
      setClasses(data);
    };
    fetchClasses();
  }, []);

  const fetchStudents = async (classId) => {
    setSelectedClass(classId);
    const response = await fetch(`http://localhost:5000/api/classes/${classId}/students`);
    const data = await response.json();
    setStudents(data);
  };

  const handleAttendanceChange = (studentId, status) => {
    setAttendance({ ...attendance, [studentId]: status });
  };

  const submitAttendance = async () => {
    await Promise.all(
      students.map(async (student) => {
        await fetch('http://localhost:5000/api/classes/attendance', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            class_id: selectedClass,
            student_id: student.id,
            status: attendance[student.id] || 'absent',
            date,
          }),
        });
      })
    );
    alert('Attendance submitted successfully');
  };

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <h1 className="text-3xl font-bold mb-6 text-center">Instructor Dashboard</h1>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Class List */}
        <div className="bg-white p-4 shadow rounded">
          <h2 className="text-xl font-semibold mb-3">Your Classes</h2>
          <ul>
            {classes.map((c) => (
              <li key={c.id} className="p-2 border-b flex justify-between">
                <span>{c.name} - {c.trimester}</span>
                <button onClick={() => fetchStudents(c.id)} className="bg-blue-500 text-white px-3 py-1 rounded">View</button>
              </li>
            ))}
          </ul>
        </div>

        {/* Attendance & Actions */}
        {selectedClass && (
          <div className="bg-white p-4 shadow rounded">
            <h2 className="text-xl font-semibold mb-3">Manage Class</h2>
            <p className="text-gray-600 mb-3">Selected Class: <strong>{selectedClass}</strong></p>
            <label className="block mb-3">Select Date:
              <input type="date" value={date} onChange={(e) => setDate(e.target.value)} className="ml-2 p-2 border rounded" />
            </label>
            <h3 className="text-lg font-semibold mb-2">Take Attendance</h3>
            <ul>
              {students.map((student) => (
                <li key={student.id} className="flex justify-between items-center p-2 border-b">
                  <span>{student.username}</span>
                  <select
                    className="p-2 border rounded"
                    value={attendance[student.id] || 'absent'}
                    onChange={(e) => handleAttendanceChange(student.id, e.target.value)}
                  >
                    <option value="present">Present</option>
                    <option value="absent">Absent</option>
                    <option value="late">Late</option>
                  </select>
                </li>
              ))}
            </ul>
            <button onClick={submitAttendance} className="w-full bg-green-500 text-white p-2 rounded mt-3">Submit Attendance</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default InstructorDashboard;
