import { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import AdminSidebar from '../../components/AdminSidebar';

const AdminDashboard = () => {
  const [stats, setStats] = useState({ students: 0, instructors: 0, classes: 0, semester: "Spring 2025" });
  const location = useLocation();


  useEffect(() => {
    fetchStats();
  }, []);

  const fetchStats = async () => {
    try {
      const response = await fetch('http://localhost:5100/api/users/stats');
      if (!response.ok) throw new Error('Failed to fetch stats');
      const data = await response.json();
      setStats(data);
    } catch (error) {
      console.error('Error fetching stats:', error);
    }
  };

  return (
    <div className="flex h-screen">
        <aside className="w-64 bg-gray-900 text-white flex flex-col p-5">
          <AdminSidebar location={location} />
        </aside>

      <main className="flex-1 p-6 bg-gray-100">
        <h1 className="text-3xl font-bold mb-6">Admin Dashboard</h1>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
          <div className="bg-white p-4 shadow rounded flex justify-between items-center">
            <div>
              <h2 className="text-lg font-semibold">Total Students</h2>
              <p className="text-3xl font-bold">{stats.students}</p>
            </div>
            <span className="text-blue-500 text-4xl">🎓</span>
          </div>

          <div className="bg-white p-4 shadow rounded flex justify-between items-center">
            <div>
              <h2 className="text-lg font-semibold">Total Instructors</h2>
              <p className="text-3xl font-bold">{stats.instructors}</p>
            </div>
            <span className="text-green-500 text-4xl">👨‍🏫</span>
          </div>

          <div className="bg-white p-4 shadow rounded flex justify-between items-center">
            <div>
              <h2 className="text-lg font-semibold">Total Classes</h2>
              <p className="text-3xl font-bold">{stats.classes}</p>
            </div>
            <span className="text-yellow-500 text-4xl">🏫</span>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <Link to="/admin/users" className="bg-blue-500 text-white p-4 rounded text-center hover:bg-blue-600">👥 Manage Users</Link>
          <Link to="/admin/classes" className="bg-green-500 text-white p-4 rounded text-center hover:bg-green-600">🏫 Manage Classes</Link>
        </div>
      </main>
    </div>
  );
};

export default AdminDashboard;
