import { useState, useEffect } from 'react';

const AdminDashboard = () => {
  const [className, setClassName] = useState('');
  const [description, setDescription] = useState('');
  const [instructorId, setInstructorId] = useState('');
  const [trimester, setTrimester] = useState('');
  const [instructors, setInstructors] = useState([]);
  const [message, setMessage] = useState('');

  useEffect(() => {
    // Fetch instructors from the database
    const fetchInstructors = async () => {
      const response = await fetch('http://localhost:5000/api/users/instructors');
      const data = await response.json();
      setInstructors(data);
    };

    fetchInstructors();
  }, []);

  const handleCreateClass = async (e) => {
    e.preventDefault();

    const response = await fetch('http://localhost:5000/api/classes/create', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ name: className, description, instructor_id: instructorId, trimester }),
    });

    const data = await response.json();
    if (!response.ok) {
      setMessage(`Error: ${data.message}`);
    } else {
      setMessage(`Class created successfully: ${data.class.name}`);
      setClassName('');
      setDescription('');
      setInstructorId('');
      setTrimester('');
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-4">Admin Dashboard</h1>

      <h2 className="text-xl font-semibold mb-2">Create a New Class</h2>
      {message && <p className="text-green-500">{message}</p>}

      <form onSubmit={handleCreateClass} className="bg-white p-4 rounded shadow-md">
        <input
          type="text"
          placeholder="Class Name"
          className="w-full p-2 border rounded mb-3"
          value={className}
          onChange={(e) => setClassName(e.target.value)}
          required
        />
        <textarea
          placeholder="Description"
          className="w-full p-2 border rounded mb-3"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />
        <select
          className="w-full p-2 border rounded mb-3"
          value={instructorId}
          onChange={(e) => setInstructorId(e.target.value)}
          required
        >
          <option value="">Select an Instructor</option>
          {instructors.map((instructor) => (
            <option key={instructor.id} value={instructor.id}>
              {instructor.username}
            </option>
          ))}
        </select>
        <input
          type="text"
          placeholder="Trimester (e.g., Spring 2025)"
          className="w-full p-2 border rounded mb-3"
          value={trimester}
          onChange={(e) => setTrimester(e.target.value)}
          required
        />
        <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded">
          Create Class
        </button>
      </form>
    </div>
  );
};

export default AdminDashboard;
