import { useState, useEffect } from 'react';
import { supabase } from '../lib/supabase';

export default function AttendanceTab({ levelCode }) {
  const [students, setStudents] = useState([]);
  const [attendance, setAttendance] = useState([]);
  const [error, setError] = useState(null);
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    fetchStudents();
    fetchAttendance();
  }, [levelCode, selectedDate]);

  const fetchStudents = async () => {
    try {
      const { data, error } = await supabase
        .from('student_enrollment')
        .select(`
          student:users (
            id,
            name
          )
        `)
        .eq('level_code', levelCode);

      if (error) throw error;
      setStudents(data.map(item => item.student));
    } catch (err) {
      setError(err.message);
    }
  };

  const fetchAttendance = async () => {
    try {
      const { data, error } = await supabase
        .from('attendance')
        .select('*')
        .eq('level_code', levelCode)
        .eq('date', selectedDate);

      if (error) throw error;
      setAttendance(data);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleAttendanceChange = async (studentId, status) => {
    setIsSubmitting(true);
    try {
      const existingRecord = attendance.find(record => record.student_id === studentId);

      if (existingRecord) {
        const { error } = await supabase
          .from('attendance')
          .update({ status })
          .eq('id', existingRecord.id);

        if (error) throw error;
      } else {
        const { error } = await supabase
          .from('attendance')
          .insert([{
            student_id: studentId,
            level_code: levelCode,
            date: selectedDate,
            status,
            recorded_at: new Date().toISOString()
          }]);

        if (error) throw error;
      }

      await fetchAttendance();
    } catch (err) {
      setError(err.message);
    } finally {
      setIsSubmitting(false);
    }
  };

  const getAttendanceStatus = (studentId) => {
    const record = attendance.find(record => record.student_id === studentId);
    return record?.status || 'absent';
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold bg-gradient-to-r from-indigo-600 to-purple-600 bg-clip-text text-transparent">
          Attendance
        </h2>
        <div className="flex items-center space-x-4">
          <label className="text-sm font-medium text-gray-700">Date:</label>
          <input
            type="date"
            value={selectedDate}
            onChange={(e) => setSelectedDate(e.target.value)}
            className="rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
          />
        </div>
      </div>

      {error && (
        <div className="bg-red-50 border-l-4 border-red-500 p-4">
          <p className="text-red-700">{error}</p>
        </div>
      )}

      <div className="bg-white rounded-lg shadow-md overflow-hidden">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Student
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Status
              </th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {students.map(student => (
              <tr key={student.id}>
                <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                  {student.name}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                  <div className="flex items-center space-x-4">
                    <button
                      onClick={() => handleAttendanceChange(student.id, 'present')}
                      disabled={isSubmitting}
                      className={`px-3 py-1 rounded-md text-sm font-medium ${
                        getAttendanceStatus(student.id) === 'present'
                          ? 'bg-green-100 text-green-800'
                          : 'bg-gray-100 text-gray-800 hover:bg-gray-200'
                      }`}
                    >
                      Present
                    </button>
                    <button
                      onClick={() => handleAttendanceChange(student.id, 'absent')}
                      disabled={isSubmitting}
                      className={`px-3 py-1 rounded-md text-sm font-medium ${
                        getAttendanceStatus(student.id) === 'absent'
                          ? 'bg-red-100 text-red-800'
                          : 'bg-gray-100 text-gray-800 hover:bg-gray-200'
                      }`}
                    >
                      Absent
                    </button>
                    <button
                      onClick={() => handleAttendanceChange(student.id, 'late')}
                      disabled={isSubmitting}
                      className={`px-3 py-1 rounded-md text-sm font-medium ${
                        getAttendanceStatus(student.id) === 'late'
                          ? 'bg-yellow-100 text-yellow-800'
                          : 'bg-gray-100 text-gray-800 hover:bg-gray-200'
                      }`}
                    >
                      Late
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
