import { useEffect, useState } from 'react';

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [classes, setClasses] = useState([]);
  const [newUser, setNewUser] = useState({ username: '', email: '', password: '', role: 'student' });
  const [newClass, setNewClass] = useState({ name: '', description: '', instructor_id: '', semester: '', start_date: '', end_date: '' });
  const [associations, setAssociations] = useState({ student_id: '', class_id: '', parent1_id: '', parent2_id: '' });

  useEffect(() => {
    const fetchUsersAndClasses = async () => {
      try {
        const usersRes = await fetch('http://localhost:5100/api/users/all');
        const classesRes = await fetch('http://localhost:5100/api/classes/all');
        setUsers(await usersRes.json());
        setClasses(await classesRes.json());
      } catch (error) {
        console.error('Error fetching users and classes:', error);
      }
    };
    fetchUsersAndClasses();
  }, []);

  const handleCreateUser = async () => {
    try {
      await fetch('http://localhost:5100/api/admin/create-user', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newUser),
      });
      setNewUser({ username: '', email: '', password: '', role: 'student' });
    } catch (error) {
      console.error('Error creating user:', error);
    }
  };

  const handleCreateClass = async () => {
    try {
      await fetch('http://localhost:5100/api/admin/create-class', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newClass),
      });
      setNewClass({ name: '', description: '', instructor_id: '', semester: '', start_date: '', end_date: '' });
    } catch (error) {
      console.error('Error creating class:', error);
    }
  };

  const handleAssignStudentToClass = async () => {
    try {
      await fetch('http://localhost:5100/api/admin/assign-student-to-class', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ student_id: associations.student_id, class_id: associations.class_id }),
      });
    } catch (error) {
      console.error('Error assigning student to class:', error);
    }
  };

  const handleAssignParent = async () => {
    try {
      await fetch('http://localhost:5100/api/admin/assign-parent', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ student_id: associations.student_id, parent1_id: associations.parent1_id, parent2_id: associations.parent2_id }),
      });
    } catch (error) {
      console.error('Error assigning parents:', error);
    }
  };

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <h1 className="text-3xl font-bold mb-6 text-center">Admin Dashboard</h1>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="bg-white p-4 shadow rounded">
          <h2 className="text-xl font-semibold mb-3">Create User</h2>
          <input type="text" placeholder="Username" value={newUser.username} onChange={(e) => setNewUser({ ...newUser, username: e.target.value })} className="w-full p-2 border rounded mb-2" />
          <input type="email" placeholder="Email" value={newUser.email} onChange={(e) => setNewUser({ ...newUser, email: e.target.value })} className="w-full p-2 border rounded mb-2" />
          <input type="password" placeholder="Password" value={newUser.password} onChange={(e) => setNewUser({ ...newUser, password: e.target.value })} className="w-full p-2 border rounded mb-2" />
          <select value={newUser.role} onChange={(e) => setNewUser({ ...newUser, role: e.target.value })} className="w-full p-2 border rounded mb-3">
            <option value="admin">Admin</option>
            <option value="instructor">Instructor</option>
            <option value="student">Student</option>
            <option value="parent">Parent</option>
          </select>
          <button onClick={handleCreateUser} className="w-full bg-blue-500 text-white p-2 rounded">Create User</button>
        </div>

        <div className="bg-white p-4 shadow rounded">
          <h2 className="text-xl font-semibold mb-3">Assign Student to Class</h2>
          <button onClick={handleAssignStudentToClass} className="w-full bg-green-500 text-white p-2 rounded">Assign</button>
        </div>

        <div className="bg-white p-4 shadow rounded">
          <h2 className="text-xl font-semibold mb-3">Assign Parents to Student</h2>
          <button onClick={handleAssignParent} className="w-full bg-yellow-500 text-white p-2 rounded">Assign</button>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
