import { useState, useEffect } from 'react';
import { supabase } from '../lib/supabase';
import ReactMarkdown from 'react-markdown';
import rehypeSanitize from 'rehype-sanitize';

export default function TeacherDashboard({ levelCode }) {
  const [activeTab, setActiveTab] = useState('announcements'); // 'announcements', 'assignments', 'grades'
  const [assignments, setAssignments] = useState([]);
  const [grades, setGrades] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showAssignmentForm, setShowAssignmentForm] = useState(false);
  const [showGradeForm, setShowGradeForm] = useState(false);
  const [selectedAssignment, setSelectedAssignment] = useState(null);
  const [newAssignment, setNewAssignment] = useState({
    title: '',
    description: '',
    max_score: 10,
    due_date: '',
  });
  const [newGrade, setNewGrade] = useState({
    score: '',
    feedback: '',
    student_id: null,
    assignment_id: null
  });

  useEffect(() => {
    fetchAssignments();
    fetchGrades();
  }, [levelCode]);

  const fetchAssignments = async () => {
    try {
      const { data, error } = await supabase
        .from('assignments')
        .select('*')
        .eq('level_code', levelCode)
        .order('created_at', { ascending: false });

      if (error) throw error;
      setAssignments(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const fetchGrades = async () => {
    try {
      const { data, error } = await supabase
        .from('grades')
        .select(`
          *,
          assignment:assignments(*),
          student:users(name)
        `)
        .order('graded_at', { ascending: false });

      if (error) throw error;
      setGrades(data);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleAssignmentSubmit = async (e) => {
    e.preventDefault();
    try {
      const { error } = await supabase
        .from('assignments')
        .insert([{
          ...newAssignment,
          level_code: levelCode,
          teacher_id: (await supabase.auth.getUser()).data.user.id
        }]);

      if (error) throw error;

      setNewAssignment({
        title: '',
        description: '',
        max_score: 10,
        due_date: '',
      });
      setShowAssignmentForm(false);
      fetchAssignments();
    } catch (err) {
      setError(err.message);
    }
  };

  const handleGradeSubmit = async (e) => {
    e.preventDefault();
    try {
      const { error } = await supabase
        .from('grades')
        .insert([{
          ...newGrade,
          graded_at: new Date().toISOString()
        }]);

      if (error) throw error;

      setNewGrade({
        score: '',
        feedback: '',
        student_id: null,
        assignment_id: null
      });
      setShowGradeForm(false);
      fetchGrades();
    } catch (err) {
      setError(err.message);
    }
  };

  const startGrading = (assignment) => {
    setSelectedAssignment(assignment);
    setNewGrade({
      ...newGrade,
      assignment_id: assignment.id
    });
    setShowGradeForm(true);
  };

  if (loading) return <div className="text-center py-4">Loading...</div>;
  if (error) return <div className="text-red-500 text-center py-4">{error}</div>;

  return (
    <div className="space-y-6">
      {/* Tabs */}
      <div className="border-b border-gray-200">
        <nav className="-mb-px flex space-x-8">
          {['announcements', 'assignments', 'grades'].map((tab) => (
            <button
              key={tab}
              onClick={() => setActiveTab(tab)}
              className={`${
                activeTab === tab
                  ? 'border-indigo-500 text-indigo-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              } whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm capitalize`}
            >
              {tab}
            </button>
          ))}
        </nav>
      </div>

      {/* Content */}
      <div className="mt-6">
        {activeTab === 'assignments' && (
          <div className="space-y-6">
            <div className="flex justify-between items-center">
              <h2 className="text-2xl font-bold text-gray-900">Assignments</h2>
              <button
                onClick={() => setShowAssignmentForm(true)}
                className="px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700"
              >
                New Assignment
              </button>
            </div>

            <div className="space-y-4">
              {assignments.map((assignment) => (
                <div key={assignment.id} className="bg-white rounded-lg shadow-sm p-6">
                  <div className="flex justify-between items-start">
                    <div>
                      <h3 className="text-lg font-semibold text-gray-900">{assignment.title}</h3>
                      <p className="text-sm text-gray-500">
                        Due: {new Date(assignment.due_date).toLocaleString()}
                      </p>
                    </div>
                    <div className="flex items-center space-x-2">
                      <span className="px-2 py-1 bg-indigo-100 text-indigo-700 rounded-md text-sm">
                        Max Score: {assignment.max_score}
                      </span>
                      <button
                        onClick={() => startGrading(assignment)}
                        className="text-indigo-600 hover:text-indigo-700 text-sm font-medium"
                      >
                        Grade
                      </button>
                    </div>
                  </div>

                  {assignment.description && (
                    <div className="prose prose-sm max-w-none mt-2">
                      <ReactMarkdown rehypePlugins={[rehypeSanitize]}>
                        {assignment.description}
                      </ReactMarkdown>
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>
        )}

        {activeTab === 'grades' && (
          <div className="space-y-6">
            <h2 className="text-2xl font-bold text-gray-900">Grades</h2>
            <div className="bg-white rounded-lg shadow overflow-hidden">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Student
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Assignment
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Score
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Feedback
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Graded At
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {grades.map((grade) => (
                    <tr key={grade.id}>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {grade.student?.name}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {grade.assignment?.title}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {grade.score}
                      </td>
                      <td className="px-6 py-4 text-sm text-gray-900">
                        {grade.feedback}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                        {new Date(grade.graded_at).toLocaleString()}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </div>

      {/* Assignment Form Modal */}
      {showAssignmentForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg shadow-xl w-full max-w-2xl">
            <div className="p-6">
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-xl font-semibold text-gray-800">New Assignment</h3>
                <button
                  onClick={() => setShowAssignmentForm(false)}
                  className="text-gray-500 hover:text-gray-700"
                >
                  <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
                  </svg>
                </button>
              </div>

              <form onSubmit={handleAssignmentSubmit} className="space-y-4">
                {/* ... Assignment form fields ... */}
              </form>
            </div>
          </div>
        </div>
      )}

      {/* Grade Form Modal */}
      {showGradeForm && selectedAssignment && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg shadow-xl w-full max-w-2xl">
            <div className="p-6">
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-xl font-semibold text-gray-800">
                  Grade Assignment: {selectedAssignment.title}
                </h3>
                <button
                  onClick={() => {
                    setShowGradeForm(false);
                    setSelectedAssignment(null);
                  }}
                  className="text-gray-500 hover:text-gray-700"
                >
                  <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
                  </svg>
                </button>
              </div>

              <form onSubmit={handleGradeSubmit} className="space-y-4">
                <div>
                  <label htmlFor="student_id" className="block text-sm font-medium text-gray-700 mb-1">
                    Student
                  </label>
                  <select
                    id="student_id"
                    value={newGrade.student_id || ''}
                    onChange={(e) => setNewGrade({ ...newGrade, student_id: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
                    required
                  >
                    <option value="">Select a student</option>
                    {/* Add student options here */}
                  </select>
                </div>

                <div>
                  <label htmlFor="score" className="block text-sm font-medium text-gray-700 mb-1">
                    Score (max: {selectedAssignment.max_score})
                  </label>
                  <input
                    type="number"
                    id="score"
                    value={newGrade.score}
                    onChange={(e) => setNewGrade({ ...newGrade, score: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
                    min="0"
                    max={selectedAssignment.max_score}
                    step="0.1"
                    required
                  />
                </div>

                <div>
                  <label htmlFor="feedback" className="block text-sm font-medium text-gray-700 mb-1">
                    Feedback
                  </label>
                  <textarea
                    id="feedback"
                    value={newGrade.feedback}
                    onChange={(e) => setNewGrade({ ...newGrade, feedback: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
                    rows="4"
                  />
                </div>

                <div className="flex justify-end space-x-3">
                  <button
                    type="button"
                    onClick={() => {
                      setShowGradeForm(false);
                      setSelectedAssignment(null);
                    }}
                    className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 rounded-md hover:bg-gray-200"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="px-4 py-2 text-sm font-medium text-white bg-indigo-600 rounded-md hover:bg-indigo-700"
                  >
                    Submit Grade
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
