// src/pages/ClassPage.js
import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { supabase } from '../lib/supabase';

export default function LevelPage() {
  const { levelId } = useParams();
  const [classData, setClassData] = useState(null);
  const [updates, setUpdates] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchClassData = async () => {
      console.log(levelId);
      try {
        // Fetch class metadata
        const { data: classData, error: classError } = await supabase
          .from('levels')
          .select('*')
          .eq('id', levelId)
          .single();

        console.log(classError);
        if (classError) throw classError;

        // Fetch updates for this class
        const { data: updatesData, error: updatesError } = await supabase
          .from('updates')
          .select('*')
          .eq('level_id', levelId)
          .order('created_at', { ascending: false });

        if (updatesError) throw updatesError;

        setClassData(classData);
        setUpdates(updatesData);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchClassData();
  }, [levelId]);

  if (loading) return (
    <div className="flex justify-center items-center h-screen">
      <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
    </div>
  );

  if (error) return (
    <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 mx-auto max-w-2xl mt-8">
      <p className="font-bold">Error</p>
      <p>{error}</p>
    </div>
  );

  if (!classData) return (
    <div className="bg-yellow-100 border-l-4 border-yellow-500 text-yellow-700 p-4 mx-auto max-w-2xl mt-8">
      <p className="font-bold">Not Found</p>
      <p>Class not found</p>
    </div>
  );

  return (
    <div className="max-w-4xl mx-auto px-4 py-8">
      <header className="bg-white shadow-sm rounded-lg p-6 mb-8">
        <h1 className="text-3xl font-bold text-gray-900">{classData.name}</h1>
        <p className="text-lg text-gray-600 mt-2">Level: {classData.level}</p>
      </header>

      <section className="space-y-6">
        <h2 className="text-2xl font-semibold text-gray-800">Class Updates</h2>

        {updates.length === 0 ? (
          <div className="bg-blue-50 border-l-4 border-blue-400 p-4 rounded">
            <p className="text-blue-700">No updates yet for this class.</p>
          </div>
        ) : (
          <div className="space-y-4">
            {updates.map((update) => (
              <div key={update.id} className="bg-white shadow rounded-lg overflow-hidden">
                <div className="p-6">
                  <p className="text-gray-800 whitespace-pre-line">{update.content}</p>
                  <p className="text-sm text-gray-500 mt-3">
                    {new Date(update.created_at).toLocaleDateString('en-US', {
                      weekday: 'long',
                      year: 'numeric',
                      month: 'long',
                      day: 'numeric',
                      hour: '2-digit',
                      minute: '2-digit'
                    })}
                  </p>
                </div>
              </div>
            ))}
          </div>
        )}
      </section>
    </div>
  );
}
