import { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import Modal from 'react-modal';

const ManageClasses = () => {
  const location = useLocation();
  const [classes, setClasses] = useState([]);
  const [instructors, setInstructors] = useState([]);
  const [showClassModal, setShowClassModal] = useState(false);
  const [editClassModal, setEditClassModal] = useState(false);
  const [newClass, setNewClass] = useState({ name: '', subject: '', period: '', instructor_id: '' });
  const [editClass, setEditClass] = useState(null);

  useEffect(() => {
    fetchInstructors();
  }, []);

  useEffect(() => {
    if (instructors.length > 0) {
      fetchClasses();
    }
  }, [instructors]);

  const fetchClasses = async () => {
    try {
      const response = await fetch('http://localhost:5100/api/admin/classes');
      if (!response.ok) throw new Error('Failed to fetch classes');
      const data = await response.json();
      console.log(instructors)
      const updatedClasses = data.map(classItem => {

        const instructor = instructors.find(inst => inst.id === classItem.instructor_id);
        console.log(instructor,  classItem.instructor_id)

        return {
          ...classItem,
          instructor_name: instructor ? `${instructor.firstName} ${instructor.lastName}` : 'Unknown Instructor'
        };
      });
      setClasses(updatedClasses);
    } catch (error) {
      console.error('Error fetching classes:', error);
    }
  };

  const fetchInstructors = async () => {
    try {
      const response = await fetch('http://localhost:5100/api/admin/users');
      if (!response.ok) throw new Error('Failed to fetch instructors');
      const data = await response.json();
      setInstructors(data);
    } catch (error) {
      console.error('Error fetching instructors:', error);
    }
  };

  const createClass = async () => {
    try {
      const response = await fetch('http://localhost:5100/api/admin/classes', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newClass),
      });
      if (!response.ok) throw new Error('Failed to create class');
      const data = await response.json();
      setClasses([...classes, data]);
      setShowClassModal(false);
      setNewClass({ name: '', subject: '', period: '', instructor_id: '' });
    } catch (error) {
      console.error('Error creating class:', error);
    }
  };

  return (
    <div className="flex h-screen">
      <aside className="w-64 bg-gray-900 text-white flex flex-col p-5">
        <h2 className="text-2xl font-bold mb-6">Admin Panel</h2>
        <nav className="space-y-3">
          <Link to="/admin" className={`block p-2 rounded ${location.pathname === '/admin' ? 'bg-gray-700' : 'hover:bg-gray-700'}`}>üìä Dashboard</Link>
          <Link to="/admin/users" className={`block p-2 rounded ${location.pathname === '/admin/users' ? 'bg-gray-700' : 'hover:bg-gray-700'}`}>üë• Manage Users</Link>
          <Link to="/admin/classes" className={`block p-2 rounded ${location.pathname === '/admin/classes' ? 'bg-gray-700' : 'hover:bg-gray-700'}`}>üè´ Manage Classes</Link>
        </nav>
      </aside>

      <main className="flex-1 p-6">
        <h1 className="text-3xl font-bold mb-6">Manage Classes</h1>
        <div className="flex justify-between mb-4">
          <button onClick={() => setShowClassModal(true)} className="bg-blue-500 text-white p-4 rounded text-center hover:bg-blue-600">‚ûï Create Class</button>
        </div>
        <div className="mt-6 bg-white p-6 rounded shadow">
          <h2 className="text-xl font-bold mb-4">Class List</h2>
          <table className="w-full border-collapse border border-gray-300">
            <thead>
              <tr className="bg-gray-100">
                <th className="border p-2">Class Name</th>
                <th className="border p-2">Subject</th>
                <th className="border p-2">Period</th>
                <th className="border p-2">Instructor</th>
                <th className="border p-2">Actions</th>
              </tr>
            </thead>
            <tbody>
              {classes.map(classItem => (
                <tr key={classItem.id} className="border">
                  <td className="border p-2 font-semibold">{classItem.name}</td>
                  <td className="border p-2">{classItem.subject}</td>
                  <td className="border p-2">{classItem.period}</td>
                  <td className="border p-2">{classItem.instructor_name}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <Modal isOpen={showClassModal} onRequestClose={() => setShowClassModal(false)}>
          <h2>Create Class</h2>
          <form onSubmit={(e) => { e.preventDefault(); createClass(); }}>
            <input type="text" placeholder="Class Name" value={newClass.name} onChange={(e) => setNewClass({ ...newClass, name: e.target.value })} required />
            <input type="text" placeholder="Subject" value={newClass.subject} onChange={(e) => setNewClass({ ...newClass, subject: e.target.value })} required />
            <input type="text" placeholder="Period" value={newClass.period} onChange={(e) => setNewClass({ ...newClass, period: e.target.value })} required />
            <select value={newClass.instructor_id} onChange={(e) => setNewClass({ ...newClass, instructor_id: e.target.value })} required>
              <option value="">Select Instructor</option>
              {instructors.map(inst => (
                <option key={inst.id} value={inst.id}>{`${inst.firstName} ${inst.lastName}`}</option>
              ))}
            </select>
            <button type="submit">Create Class</button>
          </form>
        </Modal>
      </main>
    </div>
  );
};

export default ManageClasses;
