import { useEffect, useState } from 'react';

const StudentDashboard = () => {
  const [classes, setClasses] = useState([]);

  useEffect(() => {
    const fetchClasses = async () => {
      const user = JSON.parse(localStorage.getItem('user'));
      const response = await fetch(`http://localhost:5000/api/classes/user-classes/${user.id}/student`);
      const data = await response.json();
      setClasses(data);
    };

    fetchClasses();
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-4">Student Dashboard</h1>
      <h2 className="text-xl mb-3">Your Classes</h2>
      <ul>
        {classes.map((c) => (
         <li key={c.id}>
         <a href={`/class/${c.id}`} className="text-blue-500 underline">{c.name}</a> - {c.trimester}
       </li>

        ))}
      </ul>
    </div>
  );
};

export default StudentDashboard;
