// src/pages/ClassPage.js
import { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import { supabase } from '../lib/supabase'

export default function LevelPage() {
  const { levelId } = useParams()
  const [classData, setClassData] = useState(null)
  const [updates, setUpdates] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)

  useEffect(() => {
    const fetchClassData = async () => {
      try {
        const { data: classData, error: classError } = await supabase
          .from('levels')
          .select('*')
          .eq('id', levelId)
          .single()

        if (classError) throw classError

        const { data: updatesData, error: updatesError } = await supabase
          .from('updates')
          .select('*')
          .eq('level_id', levelId)
          .order('created_at', { ascending: false })

        if (updatesError) throw updatesError

        setClassData(classData)
        setUpdates(updatesData)
      } catch (err) {
        setError(err.message)
      } finally {
        setLoading(false)
      }
    }

    fetchClassData()
  }, [levelId])

  if (loading) return (
    <div className="min-h-screen flex items-center justify-center">
      <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
    </div>
  )

  if (error) return (
    <div className="min-h-screen flex items-center justify-center">
      <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative" role="alert">
        <strong className="font-bold">Error: </strong>
        <span className="block sm:inline">{error}</span>
      </div>
    </div>
  )

  if (!classData) return (
    <div className="min-h-screen flex items-center justify-center">
      <div className="bg-yellow-100 border border-yellow-400 text-yellow-700 px-4 py-3 rounded relative" role="alert">
        Class not found
      </div>
    </div>
  )

  return (
    <div className="min-h-screen bg-gray-50 py-8 px-4 sm:px-6 lg:px-8">
      <div className="max-w-4xl mx-auto">
        {/* Header Section */}
        <header className="bg-white rounded-lg shadow-sm p-6 mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">{classData.name}</h1>
          <p className="text-lg text-gray-600">Level: {classData.level}</p>
        </header>

        {/* Updates Section */}
        <section className="bg-white rounded-lg shadow-sm p-6">
          <h2 className="text-2xl font-semibold text-gray-900 mb-6">Class Updates</h2>

          {updates.length === 0 ? (
            <div className="text-center py-8">
              <p className="text-gray-500 text-lg">No updates yet for this class.</p>
            </div>
          ) : (
            <div className="space-y-4">
              {updates.map((update) => (
                <div 
                  key={update.id} 
                  className="bg-gray-50 rounded-lg p-4 hover:bg-gray-100 transition-colors duration-200"
                >
                  <p className="text-gray-800 mb-2">{update.content}</p>
                  <p className="text-sm text-gray-500">
                    {new Date(update.created_at).toLocaleDateString('en-US', {
                      weekday: 'long',
                      year: 'numeric',
                      month: 'long',
                      day: 'numeric',
                      hour: '2-digit',
                      minute: '2-digit'
                    })}
                  </p>
                </div>
              ))}
            </div>
          )}
        </section>
      </div>
    </div>
  )
}
