// src/components/StudentList.jsx
import { useEffect, useState } from 'react';
import { supabase } from '../lib/supabase';

export default function StudentList() {
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showPasswordModal, setShowPasswordModal] = useState(true);
  const [password, setPassword] = useState('');
  const [passwordError, setPasswordError] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Check if already authenticated
  useEffect(() => {
    const authData = sessionStorage.getItem('student_list_auth');
    if (authData) {
      const { timestamp } = JSON.parse(authData);
      if (Date.now() - timestamp < 5 * 60 * 1000) { // 5 minutes
        setIsAuthenticated(true);
        fetchStudents();
      } else {
        sessionStorage.removeItem('student_list_auth');
      }
    }
  }, []);

  const fetchStudents = async () => {
    try {
      setLoading(true);
      const { data, error } = await supabase
        .from('students')
        .select('*')
        .order('name');

      if (error) throw error;
      setStudents(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handlePasswordSubmit = async (e) => {
    e.preventDefault();
    setPasswordError(null);

    try {
      // Replace 'YOUR_ADMIN_PASSWORD' with your actual admin password
      if (password === 'YOUR_ADMIN_PASSWORD') {
        sessionStorage.setItem('student_list_auth', JSON.stringify({
          timestamp: Date.now()
        }));
        setIsAuthenticated(true);
        setShowPasswordModal(false);
        fetchStudents();
      } else {
        setPasswordError('Invalid password');
      }
    } catch (err) {
      setPasswordError(err.message);
    }
  };

  // Password Modal Component
  const PasswordModal = () => (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-xl shadow-xl w-full max-w-md p-6">
        <div className="text-center mb-6">
          <h2 className="text-2xl font-bold text-gray-900">Admin Access Required</h2>
          <p className="text-gray-600 mt-2">Please enter the admin password to view student list</p>
        </div>

        {passwordError && (
          <div className="mb-4 p-3 bg-red-50 border-l-4 border-red-500 rounded">
            <p className="text-red-700">{passwordError}</p>
          </div>
        )}

        <form onSubmit={handlePasswordSubmit} className="space-y-4">
          <div>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
              placeholder="Enter admin password"
              required
            />
          </div>

          <button
            type="submit"
            className="w-full px-4 py-3 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
          >
            Access List
          </button>
        </form>
      </div>
    </div>
  );

  if (loading) return (
    <div className="flex justify-center items-center min-h-screen">
      <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-indigo-500"></div>
    </div>
  );

  if (error) return (
    <div className="p-4 bg-red-50 border-l-4 border-red-500 text-red-700">
      <p>{error}</p>
    </div>
  );

  if (!isAuthenticated) {
    return <PasswordModal />;
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-2xl font-bold text-gray-900 mb-6">Student List</h2>

        <div className="grid gap-4">
          {students.map((student) => (
            <div 
              key={student.id} 
              className="flex items-center justify-between p-4 bg-gray-50 rounded-lg hover:bg-gray-100 transition-colors"
            >
              <div className="flex items-center space-x-4">
                <div className="flex-shrink-0">
                  <div className="h-10 w-10 rounded-full bg-indigo-100 flex items-center justify-center">
                    <span className="text-indigo-600 font-medium">
                      {student.name.charAt(0)}
                    </span>
                  </div>
                </div>
                <div>
                  <h3 className="text-lg font-medium text-gray-900">{student.name}</h3>
                  <p className="text-sm text-gray-500">{student.email}</p>
                </div>
              </div>

              <span 
                className="text-xs bg-indigo-100 text-indigo-800 px-2 py-1 rounded-full relative cursor-pointer"
                onClick={(e) => {
                  e.currentTarget.classList.toggle('blur-none');
                  setTimeout(() => {
                    e.currentTarget.classList.add('blur-sm');
                  }, 3000);
                }}
              >
                <span className="blur-sm transition-all duration-200">{student.code}</span>
              </span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
