import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const AdminDashboard = () => {
  const [stats, setStats] = useState({ students: 0, instructors: 0, classes: 0, semester: "Spring 2025" });
  const [showUserModal, setShowUserModal] = useState(false);
  const [showClassModal, setShowClassModal] = useState(false);
  const [newUser, setNewUser] = useState({ username: '', email: '', password: '', role: 'student' });
  const [newClass, setNewClass] = useState({ name: '', description: '', instructor_id: '', semester: '' });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const response = await fetch('http://localhost:5100/api/admin/stats');
        if (!response.ok) throw new Error('Failed to fetch stats');
        const data = await response.json();
        setStats(data);
      } catch (error) {
        console.error('Error fetching stats:', error);
      }
    };
    fetchStats();
  }, []);

  const handleCreateUser = async () => {
    await fetch('http://localhost:5100/api/admin/users', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(newUser),
    });
    setShowUserModal(false);
  };

  const handleCreateClass = async () => {
    await fetch('http://localhost:5100/api/admin/classes', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(newClass),
    });
    setShowClassModal(false);
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar Navigation */}
      <aside className="w-64 bg-gray-900 text-white flex flex-col p-5">
        <h2 className="text-2xl font-bold mb-6">Admin Panel</h2>
        <nav className="space-y-3">
          <Link to="/admin" className="block p-2 hover:bg-gray-700 rounded">📊 Dashboard</Link>
          <Link to="/admin/users" className="block p-2 hover:bg-gray-700 rounded">👥 Manage Users</Link>
          <Link to="/admin/classes" className="block p-2 hover:bg-gray-700 rounded">🏫 Manage Classes</Link>
        </nav>
      </aside>

      {/* Main Content Area */}
      <main className="flex-1 p-6 bg-gray-100">
        <h1 className="text-3xl font-bold mb-6">Admin Dashboard</h1>

        {/* Summary Stats */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
          <div className="bg-white p-4 shadow rounded flex justify-between items-center">
            <div>
              <h2 className="text-lg font-semibold">Total Students</h2>
              <p className="text-3xl font-bold">{stats.students}</p>
            </div>
            <span className="text-blue-500 text-4xl">🎓</span>
          </div>

          <div className="bg-white p-4 shadow rounded flex justify-between items-center">
            <div>
              <h2 className="text-lg font-semibold">Total Instructors</h2>
              <p className="text-3xl font-bold">{stats.instructors}</p>
            </div>
            <span className="text-green-500 text-4xl">👨‍🏫</span>
          </div>
        </div>

        {/* Quick Actions */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <button onClick={() => setShowUserModal(true)} className="bg-blue-500 text-white p-4 rounded text-center hover:bg-blue-600">➕ Create User</button>
          <button onClick={() => setShowClassModal(true)} className="bg-green-500 text-white p-4 rounded text-center hover:bg-green-600">🏫 Create Class</button>
        </div>

        {/* User Modal */}
        {showUserModal && (
          <div className="fixed inset-0 flex items-center justify-center bg-gray-800 bg-opacity-50">
            <div className="bg-white p-6 rounded shadow-lg">
              <h2 className="text-xl font-bold mb-4">Create User</h2>
              <input type="text" placeholder="Username" value={newUser.username} onChange={(e) => setNewUser({ ...newUser, username: e.target.value })} className="w-full p-2 border rounded mb-2" />
              <input type="email" placeholder="Email" value={newUser.email} onChange={(e) => setNewUser({ ...newUser, email: e.target.value })} className="w-full p-2 border rounded mb-2" />
              <select value={newUser.role} onChange={(e) => setNewUser({ ...newUser, role: e.target.value })} className="w-full p-2 border rounded mb-3">
                <option value="admin">Admin</option>
                <option value="instructor">Instructor</option>
                <option value="student">Student</option>
              </select>
              <button onClick={handleCreateUser} className="bg-blue-500 text-white p-2 rounded">Create</button>
              <button onClick={() => setShowUserModal(false)} className="ml-2 p-2 text-gray-600">Cancel</button>
            </div>
          </div>
        )}

        {/* Class Modal */}
        {showClassModal && (
          <div className="fixed inset-0 flex items-center justify-center bg-gray-800 bg-opacity-50">
            <div className="bg-white p-6 rounded shadow-lg">
              <h2 className="text-xl font-bold mb-4">Create Class</h2>
              <input type="text" placeholder="Class Name" value={newClass.name} onChange={(e) => setNewClass({ ...newClass, name: e.target.value })} className="w-full p-2 border rounded mb-2" />
              <button onClick={handleCreateClass} className="bg-green-500 text-white p-2 rounded">Create</button>
              <button onClick={() => setShowClassModal(false)} className="ml-2 p-2 text-gray-600">Cancel</button>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default AdminDashboard;
