import { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { supabase } from '../lib/supabase';
import AnnouncementsTab from '../components/AnnouncementsTab';
import GradesTab from '../components/GradesTab';
import AssignmentsTab from '../components/AssignmentsTab';
import AttendanceTab from '../components/AttendanceTab';

export default function TeacherDashboard() {
  const { levelCode } = useParams();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('announcements');
  const [showPasswordModal, setShowPasswordModal] = useState(false);
  const [password, setPassword] = useState('');
  const [passwordError, setPasswordError] = useState(null);
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);

  // Check if teacher is already authenticated
  useEffect(() => {
    const checkAuth = async () => {
      const authData = sessionStorage.getItem(`teacher_auth_${levelCode}`);
      if (authData) {
        const { timestamp, user } = JSON.parse(authData);
        // Check if 5 minutes have passed
        if (Date.now() - timestamp < 5 * 60 * 1000) {
          // Still valid, set user data and proceed
          setUserData(user);
          setLoading(false);
          return;
        } else {
          // Expired, remove from session
          sessionStorage.removeItem(`teacher_auth_${levelCode}`);
        }
      }
      // Not authenticated or expired, show password modal
      setShowPasswordModal(true);
      setLoading(false);
    };

    checkAuth();
  }, [levelCode]);

  const handlePasswordSubmit = async (e) => {
    e.preventDefault();
    setPasswordError(null);

    try {
      // Check the users table for the code (passcode)
      const { data: userData, error } = await supabase
        .from('users')
        .select('id, name, username, role, code')
        .eq('code', password)
        .single();

      if (error) throw error;

      if (userData && userData.role === 'instructor') {
        // Store in session storage
        sessionStorage.setItem(`teacher_auth_${levelCode}`, JSON.stringify({
          timestamp: Date.now(),
          user: userData
        }));

        setUserData(userData);
        setShowPasswordModal(false);
      } else {
        setPasswordError('Invalid teacher code');
      }
    } catch (err) {
      setPasswordError(err.message);
    }
  };

  // Password Modal Component
  const PasswordModal = () => (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-xl shadow-xl w-full max-w-md p-6">
        <div className="text-center mb-6">
          <h2 className="text-2xl font-bold text-gray-900">Teacher Authentication</h2>
          <p className="text-gray-600 mt-2">Please enter your teacher code to access the dashboard</p>
        </div>

        {passwordError && (
          <div className="mb-4 p-3 bg-red-50 border-l-4 border-red-500 rounded">
            <p className="text-red-700">{passwordError}</p>
          </div>
        )}

        <form onSubmit={handlePasswordSubmit} className="space-y-4">
          <div>
            <input
              type="text"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
              placeholder="Enter your teacher code"
              required
              autoFocus
            />
          </div>

          <button
            type="submit"
            className="w-full px-4 py-3 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
          >
            Enter Dashboard
          </button>
        </form>
      </div>
    </div>
  );

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen bg-gradient-to-br from-indigo-50 via-purple-50 to-pink-50">
        <div className="flex flex-col items-center gap-4">
          <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-b-4 border-indigo-600"></div>
          <p className="text-indigo-600 font-medium">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  if (showPasswordModal) {
    return <PasswordModal />;
  }

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="max-w-7xl mx-auto space-y-6">
        {/* Tabs */}
        <div className="bg-white rounded-lg shadow-sm">
          <nav className="flex space-x-8 px-6">
            {['announcements', 'assignments', 'grades', 'attendance'].map((tab) => (
              <button
                key={tab}
                onClick={() => setActiveTab(tab)}
                className={`${
                  activeTab === tab
                    ? 'border-indigo-500 text-indigo-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                } whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm capitalize transition-colors duration-200`}
              >
                {tab}
              </button>
            ))}
          </nav>
        </div>

        {/* Content */}
        <div className="bg-white rounded-lg shadow-sm p-6">
          {activeTab === 'U' && <AnnouncementsTab levelCode={levelCode} teacherId={userData.id} />}
          {activeTab === 'assignments' && <AssignmentsTab levelCode={levelCode} teacherId={userData.id} />}
          {activeTab === 'grades' && <GradesTab levelCode={levelCode} teacherId={userData.id} />}
          {activeTab === 'attendance' && <AttendanceTab levelCode={levelCode} teacherId={userData.id} />}
        </div>
      </div>
    </div>
  );
}
