import { useState, useEffect } from 'react';
import ReactMarkdown from 'react-markdown';
import rehypeSanitize from 'rehype-sanitize';
import { supabase } from '../../lib/supabase';

export default function AssignmentsTab({ levelCode }) {
  const [assignments, setAssignments] = useState([]);
  const [showAssignmentForm, setShowAssignmentForm] = useState(false);
  const [selectedAssignment, setSelectedAssignment] = useState(null);
  const [newAssignment, setNewAssignment] = useState({
    title: '',
    description: '',
    max_score: 10,
    due_date: '',
  });
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchAssignments();
  }, [levelCode]);

  const fetchAssignments = async () => {
    try {
      const { data, error } = await supabase
        .from('assignments')
        .select('*')
        .eq('level_code', levelCode)
        .order('created_at', { ascending: false });

      if (error) throw error;
      setAssignments(data);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleAssignmentSubmit = async (e) => {
    e.preventDefault();
    try {
      const { error } = await supabase
        .from('assignments')
        .insert([{
          ...newAssignment,
          level_code: levelCode,
          teacher_id: (await supabase.auth.getUser()).data.user.id
        }]);

      if (error) throw error;

      setNewAssignment({
        title: '',
        description: '',
        max_score: 10,
        due_date: '',
      });
      setShowAssignmentForm(false);
      fetchAssignments();
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold bg-gradient-to-r from-indigo-600 to-purple-600 bg-clip-text text-transparent">
          Assignments
        </h2>
        <button
          onClick={() => setShowAssignmentForm(true)}
          className="px-6 py-3 rounded-lg text-white font-semibold bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-700 hover:to-purple-700 transition-all duration-200 ease-in-out"
        >
          New Assignment
        </button>
      </div>

      <div className="space-y-4">
        {assignments.map((assignment) => (
          <div key={assignment.id} className="bg-white rounded-2xl shadow-lg p-8 border border-indigo-100">
            <div className="flex justify-between items-start">
              <div>
                <h3 className="text-xl font-bold text-gray-900">{assignment.title}</h3>
                <p className="text-sm text-gray-500">
                  Due: {new Date(assignment.due_date).toLocaleString()}
                </p>
              </div>
              <div className="flex items-center space-x-2">
                <span className="px-2 py-1 bg-indigo-100 text-indigo-700 rounded-md text-sm">
                  Max Score: {assignment.max_score}
                </span>
                <button
                  onClick={() => setSelectedAssignment(assignment)}
                  className="text-indigo-600 hover:text-indigo-700 text-sm font-medium"
                >
                  Grade
                </button>
              </div>
            </div>

            {assignment.description && (
              <div className="prose prose-sm max-w-none mt-4">
                <ReactMarkdown rehypePlugins={[rehypeSanitize]}>
                  {assignment.description}
                </ReactMarkdown>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Assignment Form Modal */}
      {showAssignmentForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-2xl shadow-lg p-8 border border-indigo-100 w-full max-w-2xl">
            {/* Form content similar to AnnouncementsTab */}
          </div>
        </div>
      )}
    </div>
  );
}
