import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const ClassDetail = () => {
  const { classId } = useParams();
  const [classDetails, setClassDetails] = useState(null);
  const [students, setStudents] = useState([]);
  const [updates, setUpdates] = useState([]);
  const [newsFeed, setNewsFeed] = useState([
    { id: 1, text: "ðŸ“¢ Midterm exam scheduled for next Monday!", date: "Feb 2, 2025" },
    { id: 2, text: "ðŸ“š New homework has been uploaded.", date: "Feb 1, 2025" }
  ]);

  useEffect(() => {
    const fetchClassDetails = async () => {
      const response = await fetch(`http://localhost:5100/api/classes/${classId}`);
      const data = await response.json();
      setClassDetails(data);
    };

    const fetchStudents = async () => {
      const response = await fetch(`http://localhost:5100/api/classes/${classId}/students`);
      const data = await response.json();
      setStudents(data);
    };

    const fetchUpdates = async () => {
      const response = await fetch(`http://localhost:5100/api/classes/${classId}/updates`);
      const data = await response.json();
      setUpdates(data);
    };

    fetchClassDetails();
    fetchStudents();
    fetchUpdates();
  }, [classId]);

  if (!classDetails) return <p className="text-center text-gray-600">Loading class details...</p>;

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-3xl font-bold mb-4">{classDetails.name}</h1>
      <p className="text-gray-700 mb-6">{classDetails.description}</p>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Enrolled Students Section */}
        <div className="bg-white p-4 shadow rounded">
          <h2 className="text-xl font-semibold mb-2">Enrolled Students</h2>
          <ul>
            {students.map((student) => (
              <li key={student.id} className="py-2 border-b">{student.username}</li>
            ))}
          </ul>
        </div>

        {/* News Feed */}
        <div className="bg-white p-4 shadow rounded">
          <h2 className="text-xl font-semibold mb-2">Class Updates</h2>
          <ul>
            {updates.map((update) => (
              <li key={update.id} className="py-2 border-b">
                <p className="text-gray-800 font-semibold">{update.update_type.toUpperCase()}</p>
                <p className="text-gray-800">{update.content}</p>
                <p className="text-gray-500 text-sm">{new Date(update.created_at).toLocaleDateString()}</p>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default ClassDetail;
