import { useState, useEffect } from 'react';
import { Link, useLocation, useParams } from 'react-router-dom';

const ClassPage = () => {
  const location = useLocation();
  const { classId } = useParams();
  const [classDetails, setClassDetails] = useState(null);
  const [students, setStudents] = useState([]);
  const [updates, setUpdates] = useState([]);
  const [attendance, setAttendance] = useState({});
  const [newUpdate, setNewUpdate] = useState({ content: '', type: 'announcement' });

  useEffect(() => {
    fetchClassDetails();
    fetchStudents();
    fetchUpdates();
  }, [classId]);

  const fetchClassDetails = async () => {
    try {
      const response = await fetch(`http://localhost:5100/api/classes/${classId}`);
      if (!response.ok) throw new Error('Failed to fetch class details');
      const data = await response.json();
      setClassDetails(data);
    } catch (error) {
      console.error('Error fetching class details:', error);
    }
  };

  const fetchStudents = async () => {
    try {
      const response = await fetch(`http://localhost:5100/api/instructor/classes/${classId}/students`);
      if (!response.ok) throw new Error('Failed to fetch students');
      const data = await response.json();
      setStudents(data);
    } catch (error) {
      console.error('Error fetching students:', error);
    }
  };

  const fetchUpdates = async () => {
    try {
      const response = await fetch(`http://localhost:5100/api/instructor/classes/${classId}/updates`);
      if (!response.ok) throw new Error('Failed to fetch updates');
      const data = await response.json();
      setUpdates(data);
    } catch (error) {
      console.error('Error fetching updates:', error);
    }
  };

  const handleTakeAttendance = async (studentId, status) => {
    try {
      await fetch(`http://localhost:5100/api/instructor/attendance`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ class_id: classId, student_id: studentId, status }),
      });
      setAttendance({ ...attendance, [studentId]: status });
    } catch (error) {
      console.error('Error updating attendance:', error);
    }
  };

  const handlePostUpdate = async () => {
    try {
      await fetch(`http://localhost:5100/api/instructor/classes/${classId}/updates`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ class_id: classId, ...newUpdate }),
      });
      setNewUpdate({ content: '', type: 'announcement' });
      fetchUpdates();
    } catch (error) {
      console.error('Error posting update:', error);
    }
  };

  return (
    <div className="flex h-screen">
      <aside className="w-64 bg-gray-900 text-white flex flex-col p-5">
        <h2 className="text-2xl font-bold mb-6">Class Panel</h2>
        <nav className="space-y-3">
          <Link to="/instructor" className={`block p-2 rounded ${location.pathname === '/instructor' ? 'bg-gray-700' : 'hover:bg-gray-700'}`}>ðŸ“Š Dashboard</Link>
          <Link to="/instructor/grades" className={`block p-2 rounded ${location.pathname === '/instructor/grades' ? 'bg-gray-700' : 'hover:bg-gray-700'}`}>ðŸ“‘ Manage Grades</Link>
        </nav>
      </aside>

      <main className="flex-1 p-6">
        {classDetails && (
          <h1 className="text-3xl font-bold mb-6">{classDetails.name} - {classDetails.subject}</h1>
        )}

        <h2 className="text-xl font-bold mb-4">Students in Class</h2>
        <ul>
          {students.map((student) => (
            <li key={student.id} className="flex justify-between items-center p-2 border-b">
              {student.first_name} {student.last_name}
              <div>
                <button onClick={() => handleTakeAttendance(student.id, 'present')} className="px-3 py-1 bg-green-500 text-white rounded">Present</button>
                <button onClick={() => handleTakeAttendance(student.id, 'absent')} className="px-3 py-1 bg-red-500 text-white rounded ml-2">Absent</button>
              </div>
            </li>
          ))}
        </ul>

        <h2 className="text-xl font-bold mb-4 mt-6">Post an Update</h2>
        <select className="w-full p-2 border rounded mb-3" value={newUpdate.type} onChange={(e) => setNewUpdate({ ...newUpdate, type: e.target.value })}>
          <option value="announcement">Announcement</option>
          <option value="homework">Homework</option>
          <option value="classwork">Classwork</option>
          <option value="exam">Exam</option>
        </select>
        <textarea
          className="w-full p-2 border rounded mb-3"
          placeholder="Write your update here..."
          value={newUpdate.content}
          onChange={(e) => setNewUpdate({ ...newUpdate, content: e.target.value })}
        />
        <button onClick={handlePostUpdate} className="w-full bg-blue-500 text-white p-2 rounded">Post Update</button>

        <h2 className="text-xl font-bold mb-4 mt-6">Latest Updates</h2>
        <ul>
          {updates.map((update) => (
            <li key={update.id} className="p-2 border-b">{update.type}: {update.content}</li>
          ))}
        </ul>
      </main>
    </div>
  );
};

export default ClassPage;
