import { useState } from 'react';
import { useParams } from 'react-router-dom';
import AnnouncementsTab from '../components//AnnouncementsTab';
import AssignmentsTab from '../components//AssignmentsTab';
import GradesTab from '../components/dashboard/GradesTab';

export default function TeacherDashboard() {
  const { levelCode } = useParams();
  const [activeTab, setActiveTab] = useState('announcements');

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="max-w-7xl mx-auto space-y-6">
        {/* Tabs */}
        <div className="bg-white rounded-lg shadow-sm">
          <nav className="flex space-x-8 px-6">
            {['announcements', 'assignments', 'grades'].map((tab) => (
              <button
                key={tab}
                onClick={() => setActiveTab(tab)}
                className={`${
                  activeTab === tab
                    ? 'border-indigo-500 text-indigo-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                } whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm capitalize transition-colors duration-200`}
              >
                {tab}
              </button>
            ))}
          </nav>
        </div>

        {/* Content */}
        <div className="bg-white rounded-lg shadow-sm p-6">
          {activeTab === 'announcements' && <AnnouncementsTab levelCode={levelCode} />}
          {activeTab === 'assignments' && <AssignmentsTab levelCode={levelCode} />}
          {activeTab === 'grades' && <GradesTab />}
        </div>
      </div>
    </div>
  );
}
