import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Login from './pages/Login';
import AdminDashboard from './pages/AdminDashboard';
import InstructorDashboard from './pages/InstructorDashboard';
import StudentDashboard from './pages/StudentDashboard';
import ParentDashboard from './pages/ParentDashboard';
import ProtectedRoute from './components/ProtectedRoute';
import Signup from './pages/Signup';
import ClassDetail from './pages/ClassDetail';
import Logout from './pages/logout';

function App() {
  return (
    <Router>
      <Routes>
        <Logout/>
        <Route path="/login" element={<Login />} />

        <Route path="/admin-dashboard" element={<ProtectedRoute requiredRole="admin"><AdminDashboard /></ProtectedRoute>} />
        <Route path="/instructor-dashboard" element={<ProtectedRoute requiredRole="instructor"><InstructorDashboard /></ProtectedRoute>} />
        <Route path="/student-dashboard" element={<ProtectedRoute requiredRole="student"><StudentDashboard /></ProtectedRoute>} />
        <Route path="/parent-dashboard" element={<ProtectedRoute requiredRole="parent"><ParentDashboard /></ProtectedRoute>} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/class/:classId" element={<ClassDetail />} />


      </Routes>
    </Router>
  );
}

export default App;
