import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import {} from "./"

const StudentDashboard = () => {
  const [classes, setClasses] = useState([]);

  useEffect(() => {
    const fetchClasses = async () => {
      const user = JSON.parse(localStorage.getItem('user'));
      const response = await fetch(`http://localhost:5100/api/classes/user-classes/${user.id}/student`);
      const data = await response.json();
      setClasses(data);
    };

    fetchClasses();
  }, []);

  return (

    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-3xl font-bold mb-6 text-center">ðŸ“š Your Enrolled Classes</h1>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {classes.length > 0 ? (
          classes.map((c) => (
            <div key={c.id} className="bg-white p-4 shadow rounded">
              <h2 className="text-xl font-semibold">{c.name}</h2>
              <p className="text-gray-700">{c.trimester}</p>
              <Link to={`/class/${c.id}`} className="block text-center mt-4 bg-blue-500 text-white py-2 rounded">
                View Class
              </Link>
            </div>
          ))
        ) : (
          <p className="text-gray-600 text-center">You are not enrolled in any classes.</p>
        )}
      </div>
    </div>
  );
};

export default StudentDashboard;
