import { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';

const ManageClasses = () => {
  const location = useLocation();
  const [classes, setClasses] = useState([]);
  const [showClassModal, setShowClassModal] = useState(false);
  const [editClassModal, setEditClassModal] = useState(false);
  const [newClass, setNewClass] = useState({ name: '', subject: '', period: '', instructor_id: '' });
  const [editClass, setEditClass] = useState(null);

  useEffect(() => {
    fetchClasses();
  }, []);

  const fetchClasses = async () => {
    try {
      const response = await fetch('http://localhost:5100/api/admin/classes');
      if (!response.ok) throw new Error('Failed to fetch classes');
      const data = await response.json();
      setClasses(data);
    } catch (error) {
      console.error('Error fetching classes:', error);
    }
  };

  const handleCreateClass = async () => {
    if (!newClass.name || !newClass.subject || !newClass.period || !newClass.instructor_id) {
      alert("Please fill in all required fields");
      return;
    }
    const response = await fetch('http://localhost:5100/api/admin/classes', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(newClass),
    });
    if (response.ok) {
      fetchClasses();
    }
    setNewClass({ name: '', subject: '', period: '', instructor_id: '' });
    setShowClassModal(false);
  };

  const handleEditClass = async () => {
    if (!editClass.name || !editClass.subject || !editClass.period || !editClass.instructor_id) {
      alert("Please fill in all required fields");
      return;
    }
    const response = await fetch(`http://localhost:5100/api/admin/classes/${editClass.id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(editClass),
    });
    if (response.ok) {
      fetchClasses();
    }
    setEditClass(null);
    setEditClassModal(false);
  };

  const handleDeleteClass = async (classId) => {
    if (!window.confirm("Are you sure you want to delete this class?")) return;
    await fetch(`http://localhost:5100/api/admin/classes/${classId}`, {
      method: 'DELETE',
    });
    fetchClasses();
  };

  return (
    <div className="flex h-screen">
      <aside className="w-64 bg-gray-900 text-white flex flex-col p-5">
        <h2 className="text-2xl font-bold mb-6">Admin Panel</h2>
        <nav className="space-y-3">
          <Link to="/admin/dashboard" className={`block p-2 rounded ${location.pathname === '/admin/dashboard' ? 'bg-gray-700' : 'hover:bg-gray-700'}`}>📊 Dashboard</Link>
          <Link to="/admin/users" className={`block p-2 rounded ${location.pathname === '/admin/users' ? 'bg-gray-700' : 'hover:bg-gray-700'}`}>👥 Manage Users</Link>
          <Link to="/admin/classes" className={`block p-2 rounded ${location.pathname === '/admin/classes' ? 'bg-gray-700' : 'hover:bg-gray-700'}`}>🏫 Manage Classes</Link>
        </nav>
      </aside>

      <main className="flex-1 p-6">
        <h1 className="text-3xl font-bold mb-6">Manage Classes</h1>
        <div className="flex justify-between mb-4">
          <button onClick={() => setShowClassModal(true)} className="bg-blue-500 text-white p-4 rounded text-center hover:bg-blue-600">➕ Create Class</button>
        </div>
        <div className="mt-6 bg-white p-6 rounded shadow">
          <h2 className="text-xl font-bold mb-4">Class List</h2>
          <table className="w-full border-collapse border border-gray-300">
            <thead>
              <tr className="bg-gray-100">
                <th className="border p-2">Class Name</th>
                <th className="border p-2">Subject</th>
                <th className="border p-2">Period</th>
                <th className="border p-2">Instructor</th>
                <th className="border p-2">Actions</th>
              </tr>
            </thead>
            <tbody>
              {classes.map(classItem => (
                <tr key={classItem.id} className="border">
                  <td className="border p-2 font-semibold">{classItem.name}</td>
                  <td className="border p-2">{classItem.subject}</td>
                  <td className="border p-2">{classItem.period}</td>
                  <td className="border p-2">{classItem.instructor_id}</td>
                  <td className="border p-2 flex space-x-2">
                    <button onClick={() => { setEditClass(classItem); setEditClassModal(true); }} className="bg-yellow-500 text-white px-3 py-1 rounded">✏️ Edit</button>
                    <button onClick={() => handleDeleteClass(classItem.id)} className="bg-red-500 text-white px-3 py-1 rounded">🗑 Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Edit Class Modal */}
        {editClassModal && (
          <div className="fixed inset-0 flex items-center justify-center bg-gray-800 bg-opacity-50">
            <div className="bg-white p-6 rounded shadow-lg w-1/3">
              <h2 className="text-xl font-bold mb-4">Edit Class</h2>
              <input type="text" placeholder="Class Name" className="w-full p-2 border rounded mb-2" value={editClass.name} onChange={(e) => setEditClass({ ...editClass, name: e.target.value })} />
              <input type="text" placeholder="Subject" className="w-full p-2 border rounded mb-2" value={editClass.subject} onChange={(e) => setEditClass({ ...editClass, subject: e.target.value })} />
              <input type="text" placeholder="Period" className="w-full p-2 border rounded mb-2" value={editClass.period} onChange={(e) => setEditClass({ ...editClass, period: e.target.value })} />
              <input type="text" placeholder="Instructor ID" className="w-full p-2 border rounded mb-2" value={editClass.instructor_id} onChange={(e) => setEditClass({ ...editClass, instructor_id: e.target.value })} />
              <div className="flex justify-between">
                <button onClick={handleEditClass} className="bg-green-500 text-white px-4 py-2 rounded">Save</button>
                <button onClick={() => setEditClassModal(false)} className="bg-gray-400 text-white px-4 py-2 rounded">Cancel</button>
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default ManageClasses;
