import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const AdminDashboard = () => {
  const [stats, setStats] = useState({ students: 0, instructors: 0, classes: 0, semester: "Spring 2025" });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const response = await fetch('http://localhost:5100/api/admin/stats');
        if (!response.ok) throw new Error('Failed to fetch stats');
        const data = await response.json();
        setStats(data);
      } catch (error) {
        console.error('Error fetching stats:', error);
      }
    };
    fetchStats();
  }, []);

  return (
    <div className="flex h-screen">
      {/* Sidebar Navigation */}
      <aside className="w-64 bg-gray-900 text-white flex flex-col p-5">
        <h2 className="text-2xl font-bold mb-6">Admin Panel</h2>
        <nav className="space-y-3">
          <Link to="/admin" className="block p-2 hover:bg-gray-700 rounded">📊 Dashboard</Link>
          <Link to="/admin/users" className="block p-2 hover:bg-gray-700 rounded">👥 Manage Users</Link>
          <Link to="/admin/classes" className="block p-2 hover:bg-gray-700 rounded">🏫 Manage Classes</Link>
          <Link to="/admin/assignments" className="block p-2 hover:bg-gray-700 rounded">📚 Assignments</Link>
          <Link to="/admin/parent-student" className="block p-2 hover:bg-gray-700 rounded">🎓 Parent-Student</Link>
          <Link to="/admin/attendance" className="block p-2 hover:bg-gray-700 rounded">📈 Attendance</Link>
        </nav>
      </aside>

      {/* Main Content Area */}
      <main className="flex-1 p-6 bg-gray-100">
        <h1 className="text-3xl font-bold mb-6">Admin Dashboard</h1>

        {/* Summary Stats */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
          <div className="bg-white p-4 shadow rounded flex justify-between items-center">
            <div>
              <h2 className="text-lg font-semibold">Total Students</h2>
              <p className="text-3xl font-bold">{stats.students}</p>
            </div>
            <span className="text-blue-500 text-4xl">🎓</span>
          </div>

          <div className="bg-white p-4 shadow rounded flex justify-between items-center">
            <div>
              <h2 className="text-lg font-semibold">Total Instructors</h2>
              <p className="text-3xl font-bold">{stats.instructors}</p>
            </div>
            <span className="text-green-500 text-4xl">👨‍🏫</span>
          </div>

          <div className="bg-white p-4 shadow rounded flex justify-between items-center">
            <div>
              <h2 className="text-lg font-semibold">Total Classes</h2>
              <p className="text-3xl font-bold">{stats.classes}</p>
            </div>
            <span className="text-yellow-500 text-4xl">🏫</span>
          </div>
        </div>

        {/* Quick Actions */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <Link to="/admin/create-user" className="bg-blue-500 text-white p-4 rounded text-center hover:bg-blue-600">➕ Create User</Link>
          <Link to="/admin/create-class" className="bg-green-500 text-white p-4 rounded text-center hover:bg-green-600">🏫 Create Class</Link>
          <Link to="/admin/assign-student" className="bg-yellow-500 text-white p-4 rounded text-center hover:bg-yellow-600">🔗 Assign Student</Link>
        </div>
      </main>
    </div>
  );
};

export default AdminDashboard;
