import { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';

const ManageUsers = () => {
  const location = useLocation();
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterRole, setFilterRole] = useState('all');
  const [showUserModal, setShowUserModal] = useState(false);
  const [editUserModal, setEditUserModal] = useState(false);
  const [newUser, setNewUser] = useState({ firstName: '', lastName: '', role: 'student' });
  const [editUser, setEditUser] = useState(null);

  useEffect(() => {
    fetchUsers();
  }, []);

  useEffect(() => {
    setFilteredUsers(
      users.filter(user =>
        (filterRole === 'all' || user.role === filterRole) &&
        (user.username.toLowerCase().includes(searchTerm.toLowerCase()) ||
        `${user.firstName} ${user.lastName}`.toLowerCase().includes(searchTerm.toLowerCase()) ||
        user.role.toLowerCase().includes(searchTerm.toLowerCase()))
      )
    );
  }, [searchTerm, users, filterRole]);

  const fetchUsers = async () => {
    try {
      const response = await fetch('http://localhost:5100/api/admin/users');
      if (!response.ok) throw new Error('Failed to fetch users');
      const data = await response.json();
      setUsers(data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const handleCreateUser = async () => {
    if (!newUser.firstName || !newUser.lastName || !newUser.role) {
      alert("Please fill in all fields");
      return;
    }
    const response = await fetch('http://localhost:5100/api/admin/users', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(newUser),
    });
    if (response.ok) {
      fetchUsers();
    }
    setNewUser({ firstName: '', lastName: '', role: 'student' });
    setShowUserModal(false);
  };

  return (
    <div className="flex h-screen">
      <aside className="w-64 bg-gray-900 text-white flex flex-col p-5">
        <h2 className="text-2xl font-bold mb-6">Admin Panel</h2>
        <nav className="space-y-3">
          <Link to="/admin/dashboard" className={`block p-2 rounded ${location.pathname === '/admin/dashboard' ? 'bg-gray-700' : 'hover:bg-gray-700'}`}>📊 Dashboard</Link>
          <Link to="/admin/users" className={`block p-2 rounded ${location.pathname === '/admin/users' ? 'bg-gray-700' : 'hover:bg-gray-700'}`}>👥 Manage Users</Link>
          <Link to="/admin/classes" className={`block p-2 rounded ${location.pathname === '/admin/classes' ? 'bg-gray-700' : 'hover:bg-gray-700'}`}>🏫 Manage Classes</Link>
        </nav>
      </aside>

      <main className="flex-1 p-6">
        <h1 className="text-3xl font-bold mb-6">Manage Users</h1>
        <div className="flex justify-between mb-4">
          <input
            type="text"
            placeholder="Search users..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="p-2 border rounded w-1/3"
          />
          <select value={filterRole} onChange={(e) => setFilterRole(e.target.value)} className="p-2 border rounded">
            <option value="all">All Roles</option>
            <option value="admin">Admin</option>
            <option value="instructor">Instructor</option>
            <option value="student">Student</option>
            <option value="parent">Parent</option>
          </select>
          <button onClick={() => setShowUserModal(true)} className="bg-blue-500 text-white p-4 rounded text-center hover:bg-blue-600">➕ Create User</button>
        </div>

        {/* Create User Modal */}
        {showUserModal && (
          <div className="fixed inset-0 flex items-center justify-center bg-gray-800 bg-opacity-50">
            <div className="bg-white p-6 rounded shadow-lg w-1/3">
              <h2 className="text-xl font-bold mb-4">Create User</h2>
              <input type="text" placeholder="First Name" className="w-full p-2 border rounded mb-2" value={newUser.firstName} onChange={(e) => setNewUser({ ...newUser, firstName: e.target.value })} />
              <input type="text" placeholder="Last Name" className="w-full p-2 border rounded mb-2" value={newUser.lastName} onChange={(e) => setNewUser({ ...newUser, lastName: e.target.value })} />
              <select className="w-full p-2 border rounded mb-2" value={newUser.role} onChange={(e) => setNewUser({ ...newUser, role: e.target.value })}>
                <option value="admin">Admin</option>
                <option value="instructor">Instructor</option>
                <option value="student">Student</option>
                <option value="parent">Parent</option>
              </select>
              <div className="flex justify-between">
                <button onClick={handleCreateUser} className="bg-green-500 text-white px-4 py-2 rounded">Create</button>
                <button onClick={() => setShowUserModal(false)} className="bg-gray-400 text-white px-4 py-2 rounded">Cancel</button>
              </div>
            </div>
          </div>
        )}

        <div className="mt-6 bg-white p-6 rounded shadow">
          <h2 className="text-xl font-bold mb-4">User List</h2>
          <table className="w-full border-collapse border border-gray-300">
            <thead>
              <tr className="bg-gray-100">
                <th className="border p-2">Username</th>
                <th className="border p-2">Full Name</th>
                <th className="border p-2">Role</th>
                <th className="border p-2">Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredUsers.map(user => (
                <tr key={user.id} className="border">
                  <td className="border p-2 font-semibold">{user.username}</td>
                  <td className="border p-2">{user.firstName} {user.lastName}</td>
                  <td className="border p-2 capitalize">{user.role}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </main>
    </div>
  );
};

export default ManageUsers;
