import { useEffect, useState } from 'react';

const InstructorDashboard = () => {
  const [classes, setClasses] = useState([]);
  const [selectedClass, setSelectedClass] = useState('');
  const [students, setStudents] = useState([]);
  const [updateType, setUpdateType] = useState('announcement');
  const [updateContent, setUpdateContent] = useState('');

  useEffect(() => {
    const fetchClasses = async () => {
      const user = JSON.parse(localStorage.getItem('user'));
      const response = await fetch(`http://localhost:5100/api/classes/user-classes/${user.id}/instructor`);
      const data = await response.json();
      setClasses(data);
    };

    fetchClasses();
  }, []);

  const fetchStudents = async (classId) => {
    setSelectedClass(classId);
    const response = await fetch(`http://localhost:5100/api/classes/${classId}/students`);
    const data = await response.json();
    setStudents(data);
  };

  const handleTakeAttendance = async (studentId, status) => {
    await fetch('http://localhost:5100/api/classes/attendance', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ class_id: selectedClass, student_id: studentId, status }),
    });
  };

  const handlePostUpdate = async () => {
    const user = JSON.parse(localStorage.getItem('user'));
    await fetch(`http://localhost:5100/api/classes/ ${classId}/update`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        class_id: selectedClass,
        teacher_id: user.id,
        update_type: updateType,
        content: updateContent,
      }),
    });
    setUpdateContent('');
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-4">Instructor Dashboard</h1>

      <h2 className="text-xl font-semibold mb-2">Your Classes</h2>
      <ul>
        {classes.map((c) => (
          <li key={c.id} className="p-2 bg-gray-100 my-2 rounded cursor-pointer" onClick={() => fetchStudents(c.id)}>
            {c.name} - {c.trimester}
          </li>
        ))}
      </ul>

      {selectedClass && (
        <div className="mt-6">
          <h2 className="text-xl font-semibold mb-2">Take Attendance</h2>
          <ul>
            {students.map((student) => (
              <li key={student.id} className="flex justify-between items-center p-2 border-b">
                {student.username}
                <div>
                  <button onClick={() => handleTakeAttendance(student.id, 'present')} className="px-3 py-1 bg-green-500 text-white rounded">Present</button>
                  <button onClick={() => handleTakeAttendance(student.id, 'absent')} className="px-3 py-1 bg-red-500 text-white rounded ml-2">Absent</button>
                </div>
              </li>
            ))}
          </ul>

          <h2 className="text-xl font-semibold mb-2 mt-6">Post Class Update</h2>
          <select className="w-full p-2 border rounded mb-3" value={updateType} onChange={(e) => setUpdateType(e.target.value)}>
            <option value="announcement">Announcement</option>
            <option value="homework">Homework</option>
            <option value="classwork">Classwork</option>
            <option value="exam">Exam</option>
            <option value="question">Question</option>
          </select>
          <textarea
            className="w-full p-2 border rounded mb-3"
            placeholder="Write your update here..."
            value={updateContent}
            onChange={(e) => setUpdateContent(e.target.value)}
          />
          <button onClick={handlePostUpdate} className="w-full bg-blue-500 text-white p-2 rounded">
            Post Update
          </button>
        </div>
      )}
    </div>
  );
};

export default InstructorDashboard;
